import React, { useState, useEffect, useRef, createContext, useContext, useCallback } from 'react';
import PropTypes from 'prop-types';
import './Toast.css';

/**
 * @typedef {Object} ToastAction
 * @property {string} text - Display text for the action button
 * @property {() => void} onClick - Callback when the action is clicked
 */

/**
 * @typedef {Object} ToastOptions
 * @property {string} [title] - Optional title for the toast
 * @property {ToastAction} [action] - Optional action button configuration
 * @property {boolean} [autoClose=true] - Whether to auto-close the toast
 * @property {number} [duration=5000] - Duration in milliseconds before auto-close
 * @property {boolean} [showProgress=true] - Whether to show the progress bar
 * @property {string} [position='top-right'] - Position of the toast container
 * @property {number} [limit=5] - Maximum number of toasts to show
 * @property {string} [className] - Additional CSS class for the toast
 * @property {React.CSSProperties} [style] - Inline styles for the toast
 */

/**
 * @typedef {'info' | 'success' | 'warning' | 'error'} ToastType
 */

/**
 * @typedef {'top-right' | 'top-left' | 'bottom-right' | 'bottom-left' | 'top-center' | 'bottom-center'} ToastPosition
 */

/**
 * @type {React.Context<import('./Toast').ToastContextType>}
 */

/**
 * @typedef {Object} ToastContextType
 * @property {(message: string, options?: ToastOptions) => string} info - Show info toast
 * @property {(message: string, options?: ToastOptions) => string} success - Show success toast
 * @property {(message: string, options?: ToastOptions) => string} warning - Show warning toast
 * @property {(message: string, options?: ToastOptions) => string} error - Show error toast
 * @property {(id: string) => void} remove - Remove toast by ID
 * @property {() => void} clear - Clear all toasts
 */

/**
 * Toast context for managing toast notifications
 * @type {React.Context<ToastContextType | null>}
 */
export const ToastContext = createContext(/** @type {ToastContextType | null} */ (null));

/**
 * Hook to access toast notification methods
 * @returns {ToastContextType}
 * @throws {Error} If used outside of a ToastProvider
 */
export const useToast = () => {
  const context = useContext(ToastContext);
  if (!context) {
    throw new Error('useToast must be used within a ToastProvider');
  }
  return context;
};

/**
 * Toast notification component
 * @param {Object} props
 * @param {string} props.id - Unique identifier for the toast
 * @param {ToastType} [props.type='info'] - Type of toast
 * @param {string} props.message - Message to display
 * @param {string} [props.title] - Optional title
 * @param {(id: string) => void} props.onClose - Callback when toast is closed
 * @param {boolean} [props.autoClose=true] - Whether to auto-close the toast
 * @param {number} [props.duration=5000] - Duration in milliseconds before auto-close
 * @param {boolean} [props.showProgress=true] - Whether to show the progress bar
 * @param {ToastAction} [props.action] - Optional action button configuration
 * @param {string} [props.className] - Additional CSS class
 * @param {React.CSSProperties} [props.style] - Inline styles
 */
const Toast = ({
  id,
  type = 'info',
  message,
  title,
  onClose,
  autoClose = true,
  duration = 5000,
  showProgress = true,
  action = null,
  className = '',
  style = {}
}) => {
  const [isVisible, setIsVisible] = useState(true);
  const [progress, setProgress] = useState(100);
  const progressInterval = useRef(null);
  const toastRef = useRef(null);

  // Handle auto-close with progress bar
  useEffect(() => {
    if (!autoClose) return;

    let timer;
    const startTime = Date.now();
    const endTime = startTime + duration;
    let animationFrameId;

    const updateProgress = () => {
      const now = Date.now();
      const remainingTime = Math.max(0, endTime - now);
      const newProgress = (remainingTime / duration) * 100;

      setProgress(newProgress);

      if (remainingTime > 0) {
        animationFrameId = requestAnimationFrame(updateProgress);
      } else {
        handleClose();
      }
    };

    if (showProgress) {
      // Start the progress animation
      animationFrameId = requestAnimationFrame(updateProgress);
    } else {
      // Just use a simple timeout if progress bar is disabled
      timer = setTimeout(handleClose, duration);
    }

    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
      if (timer) {
        clearTimeout(timer);
      }
    };
  }, [autoClose, duration, showProgress]);

  // Handle pause on hover
  const [isPaused, setIsPaused] = useState(false);
  const pauseTimeoutRef = useRef(null);
  const progressRef = useRef(progress);

  // Keep ref in sync with state
  useEffect(() => {
    progressRef.current = progress;
  }, [progress]);

  const handleMouseEnter = useCallback(() => {
    if (!autoClose) return;

    // Clear any existing timeouts
    if (pauseTimeoutRef.current) {
      clearTimeout(pauseTimeoutRef.current);
      pauseTimeoutRef.current = null;
    }

    // Immediately pause the progress
    setIsPaused(true);
  }, [autoClose]);

  const handleMouseLeave = useCallback(() => {
    if (!autoClose || !isPaused) return;

    // Small delay before resuming to prevent accidental hovers
    pauseTimeoutRef.current = setTimeout(() => {
      setIsPaused(false);
    }, 100);
  }, [autoClose, isPaused]);

  const handleClose = () => {
    setIsVisible(false);
    setTimeout(() => onClose(id), 300); // Wait for animation to complete
  };

  const getIcon = () => {
    switch (type) {
      case 'success':
        return '✅';
      case 'error':
        return '❌';
      case 'warning':
        return '⚠️';
      default:
        return 'ℹ️';
    }
  };

  // Get appropriate ARIA attributes based on toast type
  const getAriaProps = () => {
    const baseProps = {
      role: 'status',
      'aria-live': 'polite',
      'aria-atomic': 'true',
    };

    if (type === 'error') {
      return {
        ...baseProps,
        role: 'alert',
        'aria-live': 'assertive',
      };
    }

    return baseProps;
  };

  return (
    <div
      ref={toastRef}
      className={`toast toast-${type} ${isVisible ? 'visible' : 'hidden'} ${className}`}
      style={style}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      {...getAriaProps()}
      data-testid={`toast-${id}`}
    >
      <div className="toast-icon" aria-hidden="true">{getIcon()}</div>
      <div className="toast-content">
        {title && (
          <div className="toast-title" id={`toast-title-${id}`}>
            {title}
          </div>
        )}
        <div
          className="toast-message"
          id={`toast-message-${id}`}
          aria-labelledby={title ? `toast-title-${id}` : undefined}
        >
          {message}
        </div>
        {action && (
          <div className="toast-action">
            <button onClick={action.onClick} className="toast-action-button">
              {action.text}
            </button>
          </div>
        )}
      </div>
      <button
        className="toast-close"
        onClick={handleClose}
        aria-label="Close notification"
        data-testid={`toast-close-${id}`}
      >
        &times;
      </button>
      {showProgress && autoClose && !isPaused && (
        <div
          className="toast-progress-container"
          role="progressbar"
          aria-valuenow={Math.round(100 - progress)}
          aria-valuemin="0"
          aria-valuemax="100"
          aria-hidden="true"
        >
          <div
            className="toast-progress-bar"
            style={{ width: `${progress}%` }}
          />
        </div>
      )}
    </div>
  );
};

/**
 * ToastContainer component to manage multiple toasts
 * @param {Object} props
 * @param {ToastPosition} [props.position='top-right'] - Position of the toast container
 * @param {number} [props.limit=5] - Maximum number of toasts to show
 * @param {string} [props.className] - Additional CSS class for the container
 * @param {React.CSSProperties} [props.style] - Inline styles for the container
 */
const ToastContainer = ({
  position = 'top-right',
  limit = 5,
  className = '',
  style = {}
}) => {
  const [toasts, setToasts] = useState([]);

  // Function to add a new toast
  const addToast = (type, message, options = {}) => {
    const id = Date.now().toString();

    // Limit the number of toasts
    setToasts(prevToasts => {
      const updatedToasts = [...prevToasts, { id, type, message, ...options }];
      return updatedToasts.slice(-limit);
    });

    return id;
  };

  // Function to remove a toast by id
  const removeToast = (id) => {
    setToasts(prevToasts => prevToasts.filter(toast => toast.id !== id));
  };

  // Function to remove all toasts
  const clearToasts = () => {
    setToasts([]);
  };

  // Create toast methods
  const toastMethods = {
    info: (message, options) => addToast('info', message, options),
    success: (message, options) => addToast('success', message, options),
    warning: (message, options) => addToast('warning', message, options),
    error: (message, options) => addToast('error', message, options),
    remove: removeToast,
    clear: clearToasts
  };

  // Expose methods to the window object for global access
  useEffect(() => {
    window.toast = toastMethods;

    return () => {
      delete window.toast;
    };
  }, []);

  if (toasts.length === 0) return null;

  // Add keyboard navigation for better accessibility
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape') {
        clearToasts();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <ToastContext.Provider value={toastMethods}>
      <div
        className={`toast-container toast-${position} ${className}`}
        style={style}
        role="region"
        aria-label="Notifications"
        data-testid="toast-container"
      >
        {toasts.map(toast => (
          <Toast
            key={toast.id}
            id={toast.id}
            type={toast.type}
            title={toast.title}
            message={toast.message}
            onClose={removeToast}
            autoClose={toast.autoClose !== undefined ? toast.autoClose : true}
            duration={toast.duration || 5000}
            showProgress={toast.showProgress !== undefined ? toast.showProgress : true}
            action={toast.action}
          />
        ))}
      </div>
    </ToastContext.Provider>
  );
};

ToastContainer.propTypes = {
  position: PropTypes.oneOf([
    'top-right',
    'top-left',
    'bottom-right',
    'bottom-left',
    'top-center',
    'bottom-center'
  ]),
  limit: PropTypes.number,
  className: PropTypes.string,
  style: PropTypes.object,
};

ToastContainer.defaultProps = {
  position: 'top-right',
  limit: 5,
  className: '',
  style: {},
};

export default ToastContainer;
