import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json'
  },
  validateStatus: function (status) {
    return status >= 200 && status < 500; // Handle only client errors as errors
  }
});

// Add request interceptor to include auth token
api.interceptors.request.use(
  config => {
    const token = localStorage.getItem('customer_token') || localStorage.getItem('vendor_token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  error => {
    return Promise.reject(error);
  }
);

// Add response interceptor to handle common errors
api.interceptors.response.use(
  response => response,
  error => {
    if (error.response) {
      // Handle 401 Unauthorized errors (token expired)
      if (error.response.status === 401) {
        localStorage.removeItem('customer_token');
        localStorage.removeItem('vendor_token');
        localStorage.removeItem('user_type');

        // Only redirect if we're in the browser environment
        if (typeof window !== 'undefined') {
          window.location.href = '/login';
        }
      }
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.error('Response error:', error.response.status, error.response.data);
      return Promise.reject({
        status: error.response.status,
        message: error.response.data.message || 'An error occurred',
        data: error.response.data
      });
    } else if (error.request) {
      // The request was made but no response was received
      console.error('Request error:', error.request);
      return Promise.reject({
        status: 0,
        message: 'Network error. Please check your connection.'
      });
    } else {
      // Something happened in setting up the request that triggered an Error
      console.error('Error:', error.message);
      return Promise.reject({
        status: 0,
        message: error.message
      });
    }
  }
);

// Auth helpers
const auth = {
  isCustomerLoggedIn: () => {
    return !!localStorage.getItem('customer_token');
  },

  isVendorLoggedIn: () => {
    return !!localStorage.getItem('vendor_token');
  },

  getUserType: () => {
    return localStorage.getItem('user_type');
  },

  logout: () => {
    localStorage.removeItem('customer_token');
    localStorage.removeItem('vendor_token');
    localStorage.removeItem('user_type');
    localStorage.removeItem('customerInfo');
    localStorage.removeItem('vendorInfo');

    // Dispatch logout event
    window.dispatchEvent(new CustomEvent('auth:logout', { 
      detail: { reason: 'user_initiated' } 
    }));

    // Redirect to home
    window.location.href = '/';
  },

  setVendorInfo: (info) => {
    localStorage.setItem('vendorInfo', JSON.stringify(info));
  },

  setCustomerInfo: (info) => {
    localStorage.setItem('customerInfo', JSON.stringify(info));
  }
};

// Vendor API functions
export const vendorAPI = {
  // Authentication
  login: async (credentials) => {
    try {
      const response = await api.post('/vendors/login', credentials);
      if (response.data.token) {
        localStorage.setItem('vendor_token', response.data.token);
        if (response.data.vendor) {
          auth.setVendorInfo(response.data.vendor);
        }
      }
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  register: (data) => api.post('/vendors/signup', data),

  // Profile management
  getProfile: () => api.get('/vendors/profile'),
  updateProfile: (data) => api.put('/vendors/profile', data),
  getIdCard: () => api.get('/vendors/id-card'),

  // Product management
  addProduct: (productData) => api.post('/products', productData),
  getProducts: () => api.get('/products/vendor/inventory'),
  updateProduct: (productId, data) => api.put(`/products/${productId}`, data),
  deleteProduct: (productId) => api.delete(`/products/${productId}`),

  // Order management
  getOrders: () => api.get('/orders/vendor'),
  getOrderById: (orderId) => api.get(`/orders/vendor/${orderId}`),
  updateOrderStatus: (orderId, status) => api.put(`/orders/${orderId}/status`, { status }),

  // Dashboard data
  getDashboardStats: () => api.get('/vendors/dashboard-stats')
};

// Customer API functions
export const customerAPI = {
  // Authentication
  login: async (credentials) => {
    try {
      const response = await api.post('/customers/login', credentials);
      if (response.data.token) {
        localStorage.setItem('customer_token', response.data.token);
        if (response.data.customer) {
          auth.setCustomerInfo(response.data.customer);
        }
      }
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  register: (data) => api.post('/customers/register', data),
  socialAuth: (data) => api.post('/customers/social-auth', data),
  forgotPassword: (email) => api.post('/customers/forgot-password', { email }),
  resetPassword: (data) => api.post('/customers/reset-password', data),

  // Profile management
  getProfile: () => api.get('/customers/profile'),
  updateProfile: (data) => api.put('/customers/profile', data),

  // Product browsing
  getAllProducts: (params) => api.get('/products', { params }),
  getProductById: (productId) => api.get(`/products/${productId}`),
  getProductsByCategory: (category, params) => api.get(`/products/category/${category}`, { params }),
  searchProducts: (query, params) => api.get(`/products/search`, { params: { ...params, q: query } }),

  // Cart management
  getCart: () => api.get('/cart'),
  addToCart: (data) => api.post('/cart/add', data),
  updateCartItem: (cartItemId, quantity) => api.put(`/cart/${cartItemId}`, { quantity }),
  removeFromCart: (cartItemId) => api.delete(`/cart/${cartItemId}`),
  clearCart: () => api.delete('/cart'),

  // Order management
  createOrder: (orderData) => api.post('/orders', orderData),
  createInStoreOrder: (orderData) => api.post('/orders/in-store', orderData),
  getCustomerOrders: () => api.get('/orders/customer'),
  getOrderById: (orderId) => api.get(`/orders/customer/${orderId}`),
  cancelOrder: (orderId) => api.put(`/orders/${orderId}/cancel`),
  submitOrderReview: (orderId, reviewData) => api.post(`/orders/${orderId}/review`, reviewData),

  // Dashboard data
  getDashboardStats: () => api.get('/customers/dashboard-stats')
};

// Public API endpoints (no auth required)
export const publicAPI = {
  getCategories: () => api.get('/categories'),
  getFeaturedProducts: () => api.get('/products/featured'),
  getNewArrivals: () => api.get('/products/new-arrivals'),
  searchProducts: (query) => api.get('/products/search', { params: { q: query } })
};

// Export auth helper functions
export const { isVendorLoggedIn, isCustomerLoggedIn, logout } = auth;

export default api;
