import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './DiscoverProducts.css';
import Cart from './Cart';

const DiscoverProducts = () => {
  const [cartItems, setCartItems] = useState([]);
  const [cartIsOpen, setCartIsOpen] = useState(false);

  const addToCart = (item) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find((cartItem) => cartItem.name === item.name);
      if (existingItem) {
        return prevItems.map((cartItem) =>
          cartItem.name === item.name
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
      } else {
        return [...prevItems, { ...item, quantity: 1 }];
      }
    });
    setCartIsOpen(true);
  };

  const closeCart = () => {
    setCartIsOpen(false);
  };

  const sampleProducts = [
    { name: 'Wireless Earbuds', price: 29.99 },
    { name: 'Smart Watch', price: 199.99 },
    { name: 'Bluetooth Speaker', price: 49.99 },
    { name: 'Laptop Stand', price: 24.99 },
  ];

  return (
    <div className="discover-container">
      {/* Header */}
      <header className="discover-header">
        <div className="logo">MarketConnect</div>
        <nav>
          <Link to="/">Home</Link>
          <Link to="/vendi/products/all">All Products</Link>
          <Link to="/vendi/cart">Cart</Link>
        </nav>
        <div className="auth-buttons">
          <button className="login-btn">Login</button>
          <button className="signup-btn">Sign Up</button>
        </div>
      </header>

      {/* Search Section */}
      <section className="discover-section">
        <h1>Discover Products</h1>
        <input type="text" placeholder="Search products..." className="search-input" />
        <div className="category-tabs">
          <Link to="/vendi/products/all"><button>All Products</button></Link>
          <Link to="/vendi/products/electronics"><button>Electronics</button></Link>
          <Link to="/vendi/products/clothing"><button>Clothing</button></Link>
          <Link to="/vendi/products/home-garden"><button>Home & Garden</button></Link>
          <Link to="/vendi/products/beauty"><button>Beauty</button></Link>
          <Link to="/vendi/products/food"><button>Food</button></Link>
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-products">
        <h2>Featured Products</h2>
        <div className="product-grid">
          {sampleProducts.map((item, index) => (
            <div className="product-card" key={index}>
              <h3>{item.name}</h3>
              <p>${item.price.toFixed(2)}</p>
              <div className="card-buttons">
                <button className="add-cart" onClick={() => addToCart(item)}>Add to Cart</button>
                <button className="details">Details</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Cart Modal */}
      {cartIsOpen && (
        <div className="cart-overlay">
          <div className="cart-modal">
            <h2>Your Shopping Cart</h2>
            <button className="close-btn" onClick={closeCart}>×</button>
            <ul>
              {cartItems.map((item, index) => (
                <li key={index}>
                  {item.name} - ${item.price.toFixed(2)} × {item.quantity}
                </li>
              ))}
            </ul>
            <div className="cart-total">
              Total: $
              {cartItems.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2)}
            </div>
            <button className="checkout-btn">Proceed to Checkout</button>
          </div>
        </div>
      )}

      {/* New Arrivals */}
      <section className="new-arrivals">
        <h2>New Arrivals</h2>
        <div className="image-grid">
          {[1, 2, 3].map((img) => (
            <img key={img} src="https://via.placeholder.com/200" alt="New Arrival" />
          ))}
        </div>
      </section>

      {/* All Products Table */}
      <section className="all-products">
        <h2>All Products</h2>
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Price</th>
              <th>Category</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            <tr>
