import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import { Link } from 'react-router-dom';
import MainLayout from './layout/MainLayout';
import ProductCard from './ProductCard';
import './Home.css';

// This component is the landing page of the MarketConnect marketplace platform
// It showcases both vendor and customer (Vendi) interfaces

function Home() {
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [trendingProducts, setTrendingProducts] = useState([]);
  const [flashDeals, setFlashDeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [newArrivals, setNewArrivals] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);

  // Auto-rotate testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Fetch trending products and flash deals
  useEffect(() => {
    const fetchHomeData = async () => {
      try {
        setLoading(true);
        setError(null);

        const [trendingRes, dealsRes] = await Promise.all([
          api.get('/products/trending'),
          api.get('/products/flash-deals')
        ]);

        console.log('Trending response:', trendingRes);
        console.log('Deals response:', dealsRes);

        setTrendingProducts(trendingRes?.data || []);
        setFlashDeals(dealsRes?.data || []);
      } catch (err) {
        console.error('Home page data fetch error:', err);
        console.error('Error details:', {
          message: err.message,
          response: err.response,
          stack: err.stack
        });
        setError(
          err.response?.data?.message ||
          err.response?.data?.error ||
          err.message ||
          'Failed to load home page data. Please try again later.'
        );
      } finally {
        setLoading(false);
      }
    };

    fetchHomeData();
  }, []);

  useEffect(() => {
    // Fetch products data
    const fetchProducts = async () => {
      try {
        // Replace with actual API calls
        const response = await fetch('/api/products');
        const data = await response.json();

        setFeaturedProducts(data.featured);
        setNewArrivals(data.newArrivals);
        setBestSellers(data.bestSellers);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const features = [
    {
      id: 1,
      title: 'Discover Products',
      description: 'Browse thousands of quality products across multiple categories.',
      icon: 'ðŸ›ï¸',
      link: '/vendi/all-products',
      color: 'var(--primary-color)'
    },
    {
      id: 2,
      title: 'Become a Vendor',
      description: 'Start selling your products and reach thousands of customers.',
      icon: 'ðŸª',
      link: '/vendor/register',
      color: 'var(--accent-color)'
    },
    {
      id: 3,
      title: 'Secure Payments',
      description: 'Safe and secure payment options for all transactions.',
      icon: 'ðŸ”’',
      link: '/about',
      color: 'var(--success)'
    },
    {
      id: 4,
      title: 'Fast Delivery',
      description: 'Get your products delivered quickly to your doorstep.',
      icon: 'ðŸšš',
      link: '/about',
      color: 'var(--warning)'
    }
  ];

  const categories = [
    { id: 1, name: 'Home & Garden', icon: 'ðŸ¡', link: '/vendi/home-garden', color: '#4CAF50' },
    { id: 2, name: 'Beauty', icon: 'ðŸ’„', link: '/vendi/beauty', color: '#E91E63' },
    { id: 3, name: 'Food', icon: 'ðŸ”', link: '/vendi/food', color: '#FF9800' },
    { id: 4, name: 'Electronics', icon: 'ðŸ“±', link: '/vendi/electronics', color: '#2196F3' },
    { id: 5, name: 'Fashion', icon: 'ðŸ‘—', link: '/vendi/fashion', color: '#9C27B0' },
    { id: 6, name: 'Health', icon: 'ðŸ’ª', link: '/vendi/health', color: '#00BCD4' }
  ];

  const testimonials = [
    {
      id: 1,
      content: "MarketConnect has transformed my small business. I've reached customers I never thought possible!",
      name: "John Davis",
      role: "Vendor, Organic Foods",
      avatar: "ðŸ‘¨â€ðŸ’¼"
    },
    {
      id: 2,
      content: "The variety of products and ease of shopping makes this my go-to marketplace for everything I need.",
      name: "Sarah Miller",
      role: "Customer",
      avatar: "ðŸ‘©â€ðŸ’¼"
    },
    {
      id: 3,
      content: "From registration to sales, the entire process is streamlined. I couldn't be happier with the results.",
      name: "Michael Chen",
      role: "Vendor, Kitchen Supplies",
      avatar: "ðŸ‘¨â€ðŸ³"
    }
  ];

  if (loading) {
    return (
      <MainLayout userType="guest" showContainer={false}>
        <div className="loading-spinner">Loading...</div>
      </MainLayout>
    );
  }

  if (error) {
    return (
      <MainLayout userType="guest" showContainer={false}>
        <div className="error-message">
          <span>{error}</span>
          <button onClick={() => window.location.reload()}>
            Try Again
          </button>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout userType="guest" showContainer={false}>
      <div className="home-container">
        <section className="hero-section">
