import React, { Component } from 'react';
import './ErrorBoundary.css';

class ErrorBoundary extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      hasError: false,
      error: null,
      errorInfo: null,
      errorCount: 0
    };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // Log the error to an error reporting service
    console.error('Error caught by ErrorBoundary:', error, errorInfo);
    
    // Update state with error details
    this.setState(prevState => ({
      error: error,
      errorInfo: errorInfo,
      errorCount: prevState.errorCount + 1
    }));
    
    // Send error to backend logging service (if implemented)
    this.logErrorToService(error, errorInfo);
  }
  
  logErrorToService(error, errorInfo) {
    // This could be implemented to send errors to a backend service
    // or a third-party error tracking service like Sentry
    try {
      const errorData = {
        message: error.toString(),
        componentStack: errorInfo.componentStack,
        timestamp: new Date().toISOString(),
        url: window.location.href,
        userAgent: navigator.userAgent
      };
      
      // Example of sending to a backend endpoint
      // Uncomment when backend endpoint is ready
      /*
      fetch('/api/error-logging', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(errorData),
      });
      */
      
      // For now, just log to console in a structured way
      console.table(errorData);
    } catch (loggingError) {
      console.error('Failed to log error:', loggingError);
    }
  }

  resetError = () => {
    this.setState({ 
      hasError: false,
      error: null,
      errorInfo: null
    });
  }
  
  goToHomePage = () => {
    window.location.href = '/';
  }
  
  render() {
    const { fallback, showDetails = false, showHomeButton = true } = this.props;
    
    // If a custom fallback component is provided, use it
    if (this.state.hasError && fallback) {
      return fallback(this.state.error, this.resetError);
    }
    
    if (this.state.hasError) {
      // Default error UI
      return (
        <div className="error-boundary">
          <div className="error-container">
            <div className="error-icon">⚠️</div>
            <h2>Oops! Something went wrong</h2>
            <p>We're sorry, but there was an error loading this component.</p>
            
            {/* Show different messages based on error count */}
            {this.state.errorCount > 1 && (
              <div className="error-persistence-warning">
                <p>This error has occurred {this.state.errorCount} times. You may want to try a different action.</p>
              </div>
            )}
            
            {showDetails && this.state.error && (
              <div className="error-details">
                <details>
                  <summary>Error Details (for developers)</summary>
                  <p className="error-message">{this.state.error.toString()}</p>
                  <pre className="stack-trace">
                    {this.state.errorInfo && this.state.errorInfo.componentStack}
                  </pre>
                </details>
              </div>
            )}
            
            <div className="error-actions">
              <button 
                onClick={() => window.location.reload()} 
                className="btn btn-primary"
              >
                Reload Page
              </button>
              <button 
                onClick={this.resetError} 
                className="btn btn-outline"
              >
                Try Again
              </button>
              {showHomeButton && (
                <button 
                  onClick={this.goToHomePage} 
                  className="btn btn-secondary"
                >
                  Go to Home
                </button>
              )}
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
