import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  items: [],
  loading: false,
  error: null,
};

const wishlistSlice = createSlice({
  name: 'wishlist',
  initialState,
  reducers: {
    addToWishlistStart(state) {
      state.loading = true;
      state.error = null;
    },
    addToWishlistSuccess(state, action) {
      state.loading = false;
      if (!state.items.some(item => item.id === action.payload.id)) {
        state.items.push(action.payload);
      }
    },
    addToWishlistFailure(state, action) {
      state.loading = false;
      state.error = action.payload;
    },
    removeFromWishlistStart(state) {
      state.loading = true;
      state.error = null;
    },
    removeFromWishlistSuccess(state, action) {
      state.loading = false;
      state.items = state.items.filter(item => item.id !== action.payload);
    },
    removeFromWishlistFailure(state, action) {
      state.loading = false;
      state.error = action.payload;
    },
    fetchWishlistStart(state) {
      state.loading = true;
      state.error = null;
    },
    fetchWishlistSuccess(state, action) {
      state.loading = false;
      state.items = action.payload;
    },
    fetchWishlistFailure(state, action) {
      state.loading = false;
      state.error = action.payload;
    },
    clearWishlist(state) {
      state.items = [];
      state.loading = false;
      state.error = null;
    },
  },
});

export const {
  addToWishlistStart,
  addToWishlistSuccess,
  addToWishlistFailure,
  removeFromWishlistStart,
  removeFromWishlistSuccess,
  removeFromWishlistFailure,
  fetchWishlistStart,
  fetchWishlistSuccess,
  fetchWishlistFailure,
  clearWishlist,
} = wishlistSlice.actions;

export default wishlistSlice.reducer;

// Thunks
export const addToWishlist = (product) => async (dispatch) => {
  try {
    dispatch(addToWishlistStart());
    // Here you would typically make an API call to add to wishlist
    // const response = await api.addToWishlist(product.id);
    dispatch(addToWishlistSuccess(product));
  } catch (error) {
    dispatch(addToWishlistFailure(error.message));
  }
};

export const removeFromWishlist = (productId) => async (dispatch) => {
  try {
    dispatch(removeFromWishlistStart());
    // Here you would typically make an API call to remove from wishlist
    // await api.removeFromWishlist(productId);
    dispatch(removeFromWishlistSuccess(productId));
  } catch (error) {
    dispatch(removeFromWishlistFailure(error.message));
  }
};

export const fetchWishlist = () => async (dispatch) => {
  try {
    dispatch(fetchWishlistStart());
    // Here you would typically fetch the wishlist from the API
    // const response = await api.getWishlist();
    // dispatch(fetchWishlistSuccess(response.data));
  } catch (error) {
    dispatch(fetchWishlistFailure(error.message));
  }
};
