import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { addToCart } = useCart();

  const {
    id,
    name,
    price,
    images,
    category,
    rating,
    reviewCount,
    stock,
    discount,
  } = product;

  const handleAddToCart = async () => {
    setIsLoading(true);
    try {
      await addToCart(product);
      // Show success toast or notification
    } catch (error) {
      console.error('Failed to add to cart:', error);
      // Show error toast or notification
    } finally {
      setIsLoading(false);
    }
  };

  const renderRatingStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < 5; i++) {
      if (i < fullStars) {
        stars.push(<i key={i} className="fas fa-star"></i>);
      } else if (i === fullStars && hasHalfStar) {
        stars.push(<i key={i} className="fas fa-star-half-alt"></i>);
      } else {
        stars.push(<i key={i} className="far fa-star"></i>);
      }
    }

    return stars;
  };

  return (
    <div
      className={`product-card ${isHovered ? 'product-card-hover' : ''}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="product-card-badge">
        {discount > 0 && (
          <span className="discount-badge">-{discount}%</span>
        )}
        {stock <= 5 && stock > 0 && (
          <span className="stock-badge">Only {stock} left</span>
        )}
        {stock === 0 && (
          <span className="out-of-stock-badge">Out of Stock</span>
        )}
      </div>

      <Link to={`/vendi/product-details/${id}`} className="product-image-container">
        <img
          src={images[0]}
          alt={name}
          className="product-image"
          loading="lazy"
        />
        {images[1] && (
          <img
            src={images[1]}
            alt={name}
            className="product-image-hover"
            loading="lazy"
          />
        )}
      </Link>

      <div className="product-content">
        <Link to={`/vendi/product-details/${id}`} className="product-name">
          {name}
        </Link>

        <div className="product-category">
          <Link to={`/vendi/${category.toLowerCase()}`}>
            {category}
          </Link>
        </div>

        <div className="product-rating">
          <div className="stars">
            {renderRatingStars(rating)}
          </div>
          <span className="review-count">({reviewCount})</span>
        </div>

        <div className="product-price">
          {discount > 0 ? (
            <>
              <span className="current-price">
                ${(price * (1 - discount / 100)).toFixed(2)}
              </span>
              <span className="original-price">${price.toFixed(2)}</span>
            </>
          ) : (
            <span className="current-price">${price.toFixed(2)}</span>
          )}
        </div>

        <button
          className={`add-to-cart-btn ${isLoading ? 'loading' : ''}`}
          onClick={handleAddToCart}
          disabled={stock === 0 || isLoading}
        >
          {isLoading ? (
            <span className="loading-spinner"></span>
          ) : stock === 0 ? (
            'Out of Stock'
          ) : (
            <>
              <i className="fas fa-shopping-cart"></i>
              Add to Cart
            </>
          )}
        </button>
      </div>

      <div className="product-actions">
        <button className="action-btn wishlist-btn" title="Add to Wishlist">
          <i className="far fa-heart"></i>
        </button>
        <button className="action-btn compare-btn" title="Compare">
          <i className="fas fa-exchange-alt"></i>
        </button>
        <button className="action-btn quick-view-btn" title="Quick View">
          <i className="fas fa-eye"></i>
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
