import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import { Link } from 'react-router-dom';
import MainLayout from './layout/MainLayout';
import ProductCard from './ProductCard';
import './Home.css';

// This component is the landing page of the MarketConnect marketplace platform
// It showcases both vendor and customer (Vendi) interfaces

function Home() {
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [trendingProducts, setTrendingProducts] = useState([]);
  const [flashDeals, setFlashDeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [newArrivals, setNewArrivals] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);

  // Auto-rotate testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Fetch trending products and flash deals
  useEffect(() => {
    const fetchHomeData = async () => {
      try {
        setLoading(true);
        setError(null);

        const [trendingRes, dealsRes] = await Promise.all([
          api.get('/products/trending'),
          api.get('/products/flash-deals')
        ]);

        console.log('Trending response:', trendingRes);
        console.log('Deals response:', dealsRes);

        setTrendingProducts(trendingRes?.data || []);
        setFlashDeals(dealsRes?.data || []);
      } catch (err) {
        console.error('Home page data fetch error:', err);
        console.error('Error details:', {
          message: err.message,
          response: err.response,
          stack: err.stack
        });
        setError(
          err.response?.data?.message ||
          err.response?.data?.error ||
          err.message ||
          'Failed to load home page data. Please try again later.'
        );
      } finally {
        setLoading(false);
      }
    };

    fetchHomeData();
  }, []);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const [featuredRes, trendingRes, newArrivalsRes] = await Promise.all([
          api.get('/products/featured'),
          api.get('/products/trending'),
          api.get('/products/new-arrivals')
        ]);

        setFeaturedProducts(featuredRes.data || []);
        setTrendingProducts(trendingRes.data || []);
        setNewArrivals(newArrivalsRes.data || []);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError('Failed to load products. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const features = [
    {
      id: 1,
      icon: 'üöö',
      title: 'Free Shipping',
      description: 'On orders over $50'
    },
    {
      id: 2,
      icon: 'üîÑ',
      title: 'Easy Returns',
      description: '30 days return policy'
    },
    {
      id: 3,
      icon: 'üîí',
      title: 'Secure Payment',
      description: '100% secure payment'
    },
    {
      id: 4,
      icon: 'üí¨',
      title: '24/7 Support',
      description: 'Dedicated support'
    }
  ];

  const categories = [
    { id: 1, name: 'Electronics', image: '/images/categories/electronics.jpg' },
    { id: 2, name: 'Fashion', image: '/images/categories/fashion.jpg' },
    { id: 3, name: 'Home & Living', image: '/images/categories/home.jpg' },
    { id: 4, name: 'Beauty & Health', image: '/images/categories/beauty.jpg' },
    { id: 5, name: 'Sports & Outdoors', image: '/images/categories/sports.jpg' },
  ];

  const testimonials = [
    {
      id: 1,
      content: "MarketConnect has transformed my small business. I've reached customers I never thought possible!",
      name: "John Davis",
      role: "Vendor, Organic Foods",
      avatar: "üë®‚Äçüíº"
    },
    {
      id: 2,
      content: "The variety of products and ease of shopping makes this my go-to marketplace for everything I need.",
      name: "Sarah Miller",
      role: "Customer",
      avatar: "üë©‚Äçüíº"
    },
    {
      id: 3,
      content: "From registration to sales, the entire process is streamlined. I couldn't be happier with the results.",
      name: "Michael Chen",
      role: "Vendor, Kitchen Supplies",
      avatar: "üë®‚Äçüç≥"
    }
  ];

  const renderProductSection = (title, products, isLoading) => (
    <section className="section">
      <div className="container">
        <h2 className="section-title">{title}</h2>
        {isLoading ? (
          <div className="grid grid-cols-4">
            {[...Array(4)].map((_, index) => (
              <div key={index} className="product-card-skeleton">
                <div className="skeleton-image"></div>
                <div className="skeleton-content">
                  <div className="skeleton-title"></div>
                  <div className="skeleton-price"></div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-4">
            {products.map(product => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>
        )}
      </div>
    </section>
  );

  if (loading) {
    return (
      <MainLayout>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading products...</p>
        </div>
      </MainLayout>
    );
  }

  if (error) {
    return (
      <MainLayout>
        <div className="error-container">
          <p className="error-message">{error}</p>
          <button onClick={() => window.location.reload()} className="retry-button">
            Try Again
          </button>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout>
      <div className="home-container">
        <section className="hero-section">
          <div className="hero-content">
            <h1 className="hero-title">Welcome to MarketConnect</h1>
            <p className="hero-subtitle">The ultimate marketplace connecting vendors and customers</p>
            <div className="hero-buttons">
              <Link to="/vendi/login" className="btn btn-primary btn-lg">
                <span className="btn-icon">üõí</span> Shop Now
              </Link>
              <Link to="/vendor/register" className="btn btn-outline btn-lg">
                <span className="btn-icon">üè™</span> Become a Vendor
              </Link>
            </div>
          </div>
          <div className="hero-image">
            <div className="image-container">
              <div className="image-placeholder">
                <span className="hero-icon">üåê</span>
                <span className="marketplace-text">MarketConnect</span>
              </div>
            </div>
          </div>
        </section>

        {/* Flash Deals Section */}
        <section className="flash-deals-section">
          <h2 className="section-title">Flash Deals <span className="highlight">‚ö°</span></h2>
          <div className="deals-grid">
            {Array.isArray(flashDeals) && flashDeals.length > 0 ? flashDeals.map(deal => (
              <Link key={deal.product_id} to={`/vendi/product-details/${deal.product_id}`} className="deal-card">
                <div className="deal-image">
                  {deal.image ? (
                    <img 
                      src={deal.image} 
                      alt={deal.name} 
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/placeholder.png';
                      }}
                    />
                  ) : (
                    <div className="image-placeholder">üõçÔ∏è</div>
                  )}
                  <span className="discount-badge">-{deal.discount}%</span>
                </div>
                <div className="deal-info">
                  <h3>{deal.name}</h3>
                  <div className="price-container">
                    <span className="original-price">${deal.originalPrice}</span>
                    <span className="deal-price">${deal.dealPrice}</span>
                  </div>
                  <div className="deal-timer">
                    <span className="timer-icon">‚è∞</span>
                    <span className="time-left">{deal.timeLeft}</span>
                  </div>
                </div>
              </Link>
            )) : (
              <div className="no-data-message">No flash deals available at the moment.</div>
            )}
          </div>
        </section>

        {/* Trending Products Section */}
        <section className="trending-section">
          <h2 className="section-title">Trending Now üî•</h2>
          <div className="trending-grid">
            {Array.isArray(trendingProducts) && trendingProducts.length > 0 ? trendingProducts.map(product => (
              <Link key={product._id} to={`/vendi/product-details/${product._id}`} className="product-card">
                <div className="product-image">
                  {product.image ? (
                    <img 
                      src={product.image} 
                      alt={product.name} 
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/placeholder.png';
                      }}
                    />
                  ) : (
                    <div className="image-placeholder">üõçÔ∏è</div>
                  )}
                  {product.rating >= 4.5 && <span className="trending-badge">üî• Popular</span>}
                </div>
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p className="product-description">{product.description}</p>
                  <div className="product-meta">
                    <span className="product-price">${product.price}</span>
                    <span className="product-rating">‚≠ê {product.rating}</span>
                  </div>
                </div>
              </Link>
            )) : (
              <div className="no-data-message">No flash deals available at the moment.</div>
            )}
          </div>
        </section>

        <section className="features-section">
          <h2 className="section-title">Why Choose MarketConnect?</h2>
          <div className="features-grid">
            {features.map(feature => (
              <div key={feature.id} className="feature-card" style={{ '--card-accent': feature.color }}>
                <div className="feature-icon" style={{ color: feature.color }}>{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
                <Link to={feature.link} className="feature-link" style={{ color: feature.color }}>Learn More</Link>
              </div>
            ))}
          </div>
        </section>

        <section className="categories-section">
          <h2 className="section-title">Explore Categories</h2>
          <div className="categories-grid">
            {categories.map(category => (
              <Link key={category.id} to={`/categories/${category.name.toLowerCase()}`} className="category-card" style={{ '--category-color': category.color }}>
                <div className="category-image">
                  <img src={category.image} alt={category.name} />
                </div>
                <h3>{category.name}</h3>
              </Link>
            ))}
          </div>
        </section>

        <section className="cta-section">
          <div className="cta-content">
            <h2>Ready to start selling?</h2>
            <p>Join thousands of vendors already growing their business with MarketConnect.</p>
            <Link to="/vendor/register" className="btn btn-light btn-lg">
              <span className="btn-icon">üöÄ</span> Register as Vendor
            </Link>
          </div>
        </section>

        <section className="testimonials-section">
          <h2 className="section-title">What Our Users Say</h2>
          <div className="testimonials-slider">
            {testimonials.map((testimonial, index) => (
              <div
                key={testimonial.id}
                className={`testimonial-card ${index === activeTestimonial ? 'active' : ''}`}
                style={{ transform: `translateX(${(index - activeTestimonial) * 100}%)` }}
              >
                <div className="testimonial-content">
                  <p>"{testimonial.content}"</p>
                </div>
                <div className="testimonial-author">
                  <div className="author-avatar">{testimonial.avatar}</div>
                  <div className="author-info">
                    <h4>{testimonial.name}</h4>
                    <p>{testimonial.role}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="testimonial-indicators">
            {testimonials.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === activeTestimonial ? 'active' : ''}`}
                onClick={() => setActiveTestimonial(index)}
                aria-label={`View testimonial ${index + 1}`}
              />
            ))}
          </div>
        </section>

        <section className="newsletter-section">
          <div className="newsletter-container">
            <div className="newsletter-content">
              <h2>Subscribe to our Newsletter</h2>
              <p>Stay updated with new products, promotions, and marketplace news.</p>
              <form className="newsletter-form" onSubmit={(e) => {
                e.preventDefault();
                // Add newsletter subscription logic here
                alert('Thank you for subscribing!');
              }}>
                <div className="form-group">
                  <input
                    type="email"
                    placeholder="Enter your email address"
                    required
                  />
                  <button type="submit" className="btn btn-primary">
                    Subscribe <span className="btn-icon">‚úâÔ∏è</span>
                  </button>
                </div>
                <div className="form-check">
                  <input type="checkbox" id="newsletter-consent" required />
                  <label htmlFor="newsletter-consent">
                    I agree to receive marketing emails from MarketConnect
                  </label>
                </div>
              </form>
            </div>
          </div>
        </section>

        {/* Featured Products Section */}
        {renderProductSection('Featured Products', featuredProducts, loading)}

        {/* New Arrivals Section */}
        {renderProductSection('New Arrivals', newArrivals, loading)}

        {/* Best Sellers Section */}
        {renderProductSection('Best Sellers', bestSellers, loading)}

        {/* Features Section */}
        <section className="features">
          <div className="features-grid">
            {features.map(feature => (
              <div key={feature.id} className="feature-card">
                <div className="feature-icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </section>

        {/* Newsletter Section */}
        <section className="newsletter">
          <div className="newsletter-content">
            <h2>Subscribe to Our Newsletter</h2>
            <p>Get the latest updates on new products and upcoming sales</p>
            <form className="newsletter-form">
              <input
                type="email"
                placeholder="Enter your email"
                required
              />
              <button type="submit">Subscribe</button>
            </form>
          </div>
        </section>
      </div>
    </MainLayout>
  );
}

export default Home;
