import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Food.css';
import { customerAPI } from '../../utils/api';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import VisibilityIcon from '@mui/icons-material/Visibility';
import SentimentDissatisfiedIcon from '@mui/icons-material/SentimentDissatisfied';
import FilterListIcon from '@mui/icons-material/FilterList';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

const Food = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [filterVisible, setFilterVisible] = useState(false);
  const [priceRange, setPriceRange] = useState([0, 100]);
  const [addingToCart, setAddingToCart] = useState(null);
  const [toast, setToast] = useState({visible: false, message: '', type: ''});
  const [quickViewProduct, setQuickViewProduct] = useState(null);
  const [dietaryFilters, setDietaryFilters] = useState({
    vegetarian: false,
    vegan: false,
    glutenFree: false,
    organic: false
  });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await customerAPI.getAllProducts({ category: 'food' });
        const products = response.data.products || [];

        // Sort products based on sortBy value
        const sortedProducts = sortProducts(products, sortBy);
        setProducts(sortedProducts);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching food products:', err);
        setError('Failed to load products. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
  }, [sortBy]);

  const sortProducts = (products, criterion) => {
    const productsCopy = [...products];

    switch(criterion) {
      case 'price-low':
        return productsCopy.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
      case 'price-high':
        return productsCopy.sort((a, b) => parseFloat(b.price) - parseFloat(a.price));
      case 'popular':
        return productsCopy.sort((a, b) => b.sales_count - a.sales_count);
      case 'newest':
      default:
        return productsCopy.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
    }
  };

  const addToCart = async (productId) => {
    try {
      setAddingToCart(productId);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 800));

      // Show success toast
      setToast({
        visible: true,
        message: 'Product added to cart successfully!',
        type: 'success'
      });

      // Hide toast after 3 seconds
      setTimeout(() => {
        setToast({visible: false, message: '', type: ''});
      }, 3000);

    } catch (error) {
      console.error('Error adding to cart:', error);

      // Show error toast
      setToast({
        visible: true,
        message: 'Failed to add product to cart. Please try again.',
        type: 'error'
      });

      // Hide toast after 3 seconds
      setTimeout(() => {
        setToast({visible: false, message: '', type: ''});
      }, 3000);
    } finally {
      setAddingToCart(null);
    }
  };

  const openQuickView = (product) => {
    setQuickViewProduct(product);
  };

  const closeQuickView = () => {
    setQuickViewProduct(null);
  };

  const handleDietaryFilterChange = (filter) => {
    setDietaryFilters(prev => ({
      ...prev,
      [filter]: !prev[filter]
    }));
  };

  const filterProductsByDietary = (products) => {
    return products.filter(product => {
      // If no filters are active, show all products
      if (!Object.values(dietaryFilters).some(value => value)) {
        return true;
      }

      // Apply active filters
      const matchesFilters = (
        (dietaryFilters.vegetarian && product.is_vegetarian) ||
        (dietaryFilters.vegan && product.is_vegan) ||
        (dietaryFilters.glutenFree && product.is_gluten_free) ||
        (dietaryFilters.organic && product.is_organic)
      );

      return matchesFilters;
    });
  };

  // Apply all filters
  const filteredProducts = filterProductsByDietary(products).filter(product => {
    return product.price >= priceRange[0] && product.price <= priceRange[1];
  });

  if (loading) {
    return (
      <div className="food-container loading">
        <header className="category-header skeleton">
          <div className="skeleton-text skeleton-title"></div>
          <div className="skeleton-text skeleton-subtitle"></div>
        </header>

        <div className="skeleton-filters"></div>

        <div className="products-grid">
          {[1, 2, 3, 4, 5, 6].map(item => (
            <div className="product-card skeleton" key={item}>
              <div className="skeleton-image"></div>
              <div className="skeleton-content">
                <div className="skeleton-text skeleton-title"></div>
                <div className="skeleton-text"></div>
                <div className="skeleton-text"></div>
                <div className="skeleton-text skeleton-price"></div>
                <div className="skeleton-buttons">
                  <div className="skeleton-button"></div>
                  <div className="skeleton-button"></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="food-container">
      <header className="category-header">
        <h1>Food & Groceries</h1>
        <p>Discover fresh, organic, and delicious food products from our vendors</p>
      </header>

      <div className="breadcrumb">
        <Link to="/">Home</Link>
        <span className="separator">›</span>
        <Link to="/vendi/all-products">All Products</Link>
        <span className="separator">›</span>
        <span className="breadcrumb-current">Food & Groceries</span>
      </div>

      <div className="category-filters">
        <div className="filter-group">
          <label>Sort by:</label>
          <select 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)}
            className="sort-select"
          >
            <option value="newest">Newest</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="popular">Most Popular</option>
          </select>
        </div>

        <button 
          className="filter-toggle" 
          onClick={() => setFilterVisible(!filterVisible)}
          aria-expanded={filterVisible}
        >
          <FilterListIcon />
          <span>Filter</span>
          <KeyboardArrowDownIcon className={filterVisible ? 'expanded' : ''} />
        </button>
      </div>

      {filterVisible && (
        <div className="expanded-filters">
          <div className="filter-section">
            <h3>Price Range</h3>
            <div className="price-slider">
              <div className="price-inputs">
                <input 
                  type="number" 
                  min="0" 
                  max="100" 
                  value={priceRange[0]} 
                  onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])}
                />
                <span>to</span>
                <input 
                  type="number" 
                  min="0" 
                  max="100" 
                  value={priceRange[1]} 
                  onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                />
              </div>
            </div>
          </div>

          <div className="filter-section">
            <h3>Dietary Preferences</h3>
            <div className="filter-options">
              <label className="checkbox-label">
                <input 
                  type="checkbox" 
                  checked={dietaryFilters.vegetarian}
                  onChange={() => handleDietaryFilterChange('vegetarian')}
                />
                <span>Vegetarian</span>
              </label>
              <label className="checkbox-label">
                <input 
                  type="checkbox" 
                  checked={dietaryFilters.vegan}
                  onChange={() => handleDietaryFilterChange('vegan')}
                />
                <span>Vegan</span>
              </label>
              <label className="checkbox-label">
                <input 
                  type="checkbox" 
                  checked={dietaryFilters.glutenFree}
                  onChange={() => handleDietaryFilterChange('glutenFree')}
                />
                <span>Gluten Free</span>
              </label>
              <label className="checkbox-label">
                <input 
                  type="checkbox" 
                  checked={dietaryFilters.organic}
                  onChange={() => handleDietaryFilterChange('organic')}
                />
                <span>Organic</span>
              </label>
            </div>
          </div>

          <div className="filter-actions">
            <button 
              className="btn btn-secondary"
              onClick={() => {
                setPriceRange([0, 100]);
                setDietaryFilters({
                  vegetarian: false,
                  vegan: false,
                  glutenFree: false,
                  organic: false
                });
              }}
            >
              Reset Filters
            </button>
            <button 
              className="btn btn-primary"
              onClick={() => setFilterVisible(false)}
            >
              Apply Filters
            </button>
          </div>
        </div>
      )}

      {/* Product grid */}
      {error ? (
        <div className="error-state">
          <SentimentDissatisfiedIcon size={48} />
          <h3>Oops! Something went wrong</h3>
          <p>{error}</p>
          <button className="btn btn-primary" onClick={() => window.location.reload()}>
            Try Again
          </button>
        </div>
      ) : filteredProducts.length === 0 ? (
        <div className="empty-state">
          <SentimentDissatisfiedIcon size={48} />
          <h3>No products found</h3>
          <p>We couldn't find any food products matching your filters.</p>
          <button 
            className="btn btn-secondary" 
            onClick={() => {
              setPriceRange([0, 100]);
              setDietaryFilters({
                vegetarian: false,
                vegan: false,
                glutenFree: false,
                organic: false
              });
            }}
          >
            Reset Filters
          </button>
        </div>
      ) : (
        <div className="products-grid">
          {filteredProducts.map(product => (
            <div className="product-card" key={product.id}>
              {product.is_new && <span className="product-badge new">New</span>}
              {product.discount_percent > 0 && <span className="product-badge sale">Sale</span>}
              {product.is_organic && <span className="product-badge organic">Organic</span>}

              <div className="product-image-container">
                <img 
                  src={product.image_url || 'https://via.placeholder.com/300x200?text=Food+Item'} 
                  alt={product.name} 
                  className="product-image" 
                />
                <div className="product-overlay">
                  <button 
                    className="overlay-button cart-btn"
                    onClick={() => addToCart(product.id)}
                    disabled={addingToCart === product.id}
                    aria-label="Add to cart"
                  >
                    {addingToCart === product.id ? 'Adding...' : <ShoppingCartIcon />}
                  </button>
                  <button 
                    className="overlay-button view-btn"
                    onClick={() => openQuickView(product)}
                    aria-label="Quick view product"
                  >
                    <VisibilityIcon />
                  </button>
                </div>
              </div>

              <div className="product-details">
                <h3 className="product-title">{product.name}</h3>
                <p className="product-description">{product.description && product.description.substring(0, 60)}...</p>
                <div className="product-price">
                  {product.original_price ? (
                    <>
                      <span className="current-price">${product.price.toFixed(2)}</span>
                      <span className="original-price">${product.original_price.toFixed(2)}</span>
                    </>
                  ) : (
                    <span className="current-price">${product.price.toFixed(2)}</span>
                  )}
                </div>
                <div className="product-rating">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <span 
                      key={i} 
                      className={`star ${i < Math.floor(product.rating || 0) ? 'filled' : ''}`}
                    >
                      ★
                    </span>
                  ))}
                  <span className="rating-count">({product.review_count || 0})</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Toast Notification */}
      {toast.visible && (
        <div className={`toast-notification ${toast.type}`}>
          <span>{toast.message}</span>
          <button onClick={() => setToast({visible: false, message: '', type: ''})}>
            &times;
          </button>
        </div>
      )}

      {/* Quick View Modal */}
      {quickViewProduct && (
        <div className="quick-view-overlay" onClick={closeQuickView}>
          <div className="quick-view-modal" onClick={(e) => e.stopPropagation()}>
            <button className="close-modal" onClick={closeQuickView}>&times;</button>
            <div className="quick-view-content">
              <div className="quick-view-image">
                <img 
                  src={quickViewProduct.image_url || 'https://via.placeholder.com/400x300?text=Food+Product'} 
                  alt={quickViewProduct.name} 
                />
                {quickViewProduct.is_organic && <span className="badge organic">Organic</span>}
              </div>
              <div className="quick-view-details">
                <h2>{quickViewProduct.name}</h2>
                <div className="quick-view-price">
                  <span className="price">${quickViewProduct.price.toFixed(2)}</span>
                  {quickViewProduct.original_price && (
                    <span className="original-price">${quickViewProduct.original_price.toFixed(2)}</span>
                  )}
                </div>
                <div className="dietary-info">
                  {quickViewProduct.is_vegetarian && <span className="dietary-badge vegetarian">Vegetarian</span>}
                  {quickViewProduct.is_vegan && <span className="dietary-badge vegan">Vegan</span>}
                  {quickViewProduct.is_gluten_free && <span className="dietary-badge gluten-free">Gluten Free</span>}
                </div>
                <p className="product-description">{quickViewProduct.description || 'No description available for this product.'}</p>
                <div className="quantity-selector">
                  <button className="quantity-btn">-</button>
                  <input type="number" min="1" value="1" readOnly />
                  <button className="quantity-btn">+</button>
                </div>
                <div className="quick-view-actions">
                  <button 
                    className="btn btn-primary"
                    onClick={() => {
                      addToCart(quickViewProduct.id);
                      closeQuickView();
                    }}
                    disabled={addingToCart === quickViewProduct.id}
                  >
                    {addingToCart === quickViewProduct.id ? 'Adding...' : 'Add to Cart'}
                  </button>
                  <Link to={`/vendi/product/${quickViewProduct.id}`} className="btn btn-outline">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Food;
