import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../utils/api';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load cart items from API on mount
  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      setLoading(true);
      // Use the fetchCart function from the API utility
      const response = await api.fetchCart();
      setCartItems(response.items || []);
      setError(null);
    } catch (err) {
      setError('Failed to load cart items');
      console.error('Error fetching cart:', err);
    } finally {
      setLoading(false);
    }
  };

  const addToCart = async (product, quantity = 1) => {
    try {
      // Use the addToCart function from the API utility
      const response = await api.addToCart(product.id, quantity);
      setCartItems(response.items || []);
      return true;
    } catch (err) {
      console.error('Error adding to cart:', err);
      throw err;
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      // Use the removeFromCart function from the API utility
      const response = await api.removeFromCart(itemId);
      setCartItems(response.items || []);
    } catch (err) {
      console.error('Error removing from cart:', err);
      throw err;
    }
  };

  const updateQuantity = async (itemId, quantity) => {
    try {
      // Use the updateCartItem function from the API utility
      const response = await api.updateCartItem(itemId, quantity);
      setCartItems(response.items || []);
    } catch (err) {
      console.error('Error updating cart:', err);
      throw err;
    }
  };

  const clearCart = async () => {
    try {
      // Clear cart by removing all items
      await Promise.all(cartItems.map(item => api.removeFromCart(item.id)));
      setCartItems([]);
    } catch (err) {
      console.error('Error clearing cart:', err);
      throw err;
    }
  };

  const getCartTotal = () => {
    return cartItems.reduce((total, item) => {
      return total + (item.price * item.quantity);
    }, 0);
  };

  const getCartCount = () => {
    return cartItems.reduce((count, item) => count + item.quantity, 0);
  };

  const value = {
    cartItems,
    loading,
    error,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartCount,
    refreshCart: fetchCartItems
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
