import React, { useState } from 'react';
import { customerAPI } from '../../utils/api';
import './SocialSharing.css';

const SocialSharing = ({ productId, productName, productImage, productUrl }) => {
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [copied, setCopied] = useState(false);
  const [showQRCode, setShowQRCode] = useState(false);
  const [qrCodeUrl, setQRCodeUrl] = useState('');

  const toggleShareOptions = () => {
    setShowShareOptions(!showShareOptions);
    setCopied(false);
  };

  const handleShare = (platform) => {
    const url = encodeURIComponent(productUrl);
    const text = encodeURIComponent(`Check out this ${productName} on MarketConnect!`);
    
    let shareUrl = '';
    
    switch (platform) {
      case 'facebook':
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${url}`;
        break;
      case 'twitter':
        shareUrl = `https://twitter.com/intent/tweet?text=${text}&url=${url}`;
        break;
      case 'pinterest':
        shareUrl = `https://pinterest.com/pin/create/button/?url=${url}&media=${encodeURIComponent(productImage)}&description=${text}`;
        break;
      case 'email':
        shareUrl = `mailto:?subject=${text}&body=${text}%0A%0A${url}`;
        break;
      default:
        break;
    }
    
    if (shareUrl) {
      window.open(shareUrl, '_blank', 'width=600,height=400');
    }
    
    // Close share options after sharing
    setShowShareOptions(false);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(productUrl)
      .then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      })
      .catch(err => console.error('Failed to copy: ', err));
  };

  const generateQRCode = async () => {
    try {
      const response = await customerAPI.generateProductQRCode(productId);
    setQRCodeUrl(response.data.qrCodeUrl);
    setShowQRCode(true);
    } catch (err) {
      console.error('Error generating QR code:', err);
    }
  };

  const closeQRCode = () => {
    setShowQRCode(false);
  };

  return (
    <div className="social-sharing">
      <button 
        className="share-button"
        onClick={toggleShareOptions}
        aria-expanded={showShareOptions}
      >
        <i className="share-icon">🔗</i>
        <span>Share</span>
      </button>
      
      {showShareOptions && (
        <div className="share-options">
          <div className="share-header">
            <h4>Share this product</h4>
            <button className="close-share" onClick={toggleShareOptions}>×</button>
          </div>
          
          <div className="share-platforms">
            <button 
              className="platform-button facebook"
              onClick={() => handleShare('facebook')}
            >
              <i className="platform-icon">f</i>
              <span>Facebook</span>
            </button>
            
            <button 
              className="platform-button twitter"
              onClick={() => handleShare('twitter')}
            >
              <i className="platform-icon">t</i>
              <span>Twitter</span>
            </button>
            
            <button 
              className="platform-button pinterest"
              onClick={() => handleShare('pinterest')}
            >
              <i className="platform-icon">p</i>
              <span>Pinterest</span>
            </button>
            
            <button 
              className="platform-button email"
              onClick={() => handleShare('email')}
            >
              <i className="platform-icon">✉️</i>
              <span>Email</span>
            </button>
          </div>
          
          <div className="share-link">
            <input 
              type="text" 
              value={productUrl} 
              readOnly 
              onClick={(e) => e.target.select()}
            />
            <button 
              className="copy-button"
              onClick={copyToClipboard}
            >
              {copied ? 'Copied!' : 'Copy'}
            </button>
          </div>
          
          <button 
            className="qr-code-button"
            onClick={generateQRCode}
          >
            <i className="qr-icon">📱</i>
            <span>Generate QR Code</span>
          </button>
        </div>
      )}
      
      {showQRCode && (
        <div className="qr-code-modal">
          <div className="qr-code-content">
            <div className="qr-code-header">
              <h4>Scan to View Product</h4>
              <button className="close-qr" onClick={closeQRCode}>×</button>
            </div>
            
            <div className="qr-code-image">
              <img src={qrCodeUrl} alt="Product QR Code" />
            </div>
            
            <p className="qr-code-instructions">
              Scan this QR code with your phone's camera to view this product
            </p>
            
            <button 
              className="download-qr"
              onClick={() => {
                const link = document.createElement('a');
                link.href = qrCodeUrl;
                link.download = `${productName.replace(/\s+/g, '-')}-qr-code.png`;
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
              }}
            >
              Download QR Code
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SocialSharing;
