import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const {
    id,
    name,
    price,
    image,
    category,
    rating,
    stock,
    discount
  } = product;

  const handleAddToCart = (e) => {
    e.preventDefault();
    addToCart(product);
  };

  return (
    <Link to={`/products/${id}`} className="product-card">
      <div className="product-card-image">
        <img src={image} alt={name} />
        {discount > 0 && (
          <span className="product-card-badge">
            -{discount}%
          </span>
        )}
        <div className="product-card-actions">
          <button
            className="product-card-action"
            onClick={handleAddToCart}
            disabled={stock === 0}
          >
            {stock === 0 ? 'Out of Stock' : 'Add to Cart'}
          </button>
        </div>
      </div>

      <div className="product-card-content">
        <div className="product-card-category">{category}</div>
        <h3 className="product-card-title">{name}</h3>
        
        <div className="product-card-price">
          {discount > 0 ? (
            <>
              <span className="product-card-price-original">
                ${(price * (1 + discount / 100)).toFixed(2)}
              </span>
              <span className="product-card-price-discounted">
                ${price.toFixed(2)}
              </span>
            </>
          ) : (
            <span className="product-card-price-regular">
              ${price.toFixed(2)}
            </span>
          )}
        </div>

        <div className="product-card-rating">
          {[...Array(5)].map((_, index) => (
            <span
              key={index}
              className={`product-card-star ${
                index < Math.floor(rating) ? 'filled' : ''
              }`}
            >
              â˜…
            </span>
          ))}
          <span className="product-card-rating-count">
            ({rating.toFixed(1)})
          </span>
        </div>

        {stock > 0 && stock <= 5 && (
          <div className="product-card-stock">
            Only {stock} left in stock
          </div>
        )}
      </div>
    </Link>
  );
};

export default ProductCard;
