import React, { useState, useEffect } from 'react';
import './Toast.css';

// Toast component for individual notifications
const Toast = ({ id, type = 'info', message, onClose, autoClose = true, duration = 5000 }) => {
  const [isVisible, setIsVisible] = useState(true);
  
  useEffect(() => {
    if (autoClose) {
      const timer = setTimeout(() => {
        setIsVisible(false);
        setTimeout(() => onClose(id), 300); // Wait for animation to complete
      }, duration);
      
      return () => clearTimeout(timer);
    }
  }, [autoClose, duration, id, onClose]);
  
  const handleClose = () => {
    setIsVisible(false);
    setTimeout(() => onClose(id), 300); // Wait for animation to complete
  };
  
  const getIcon = () => {
    switch (type) {
      case 'success':
        return '✅';
      case 'error':
        return '❌';
      case 'warning':
        return '⚠️';
      default:
        return 'ℹ️';
    }
  };
  
  return (
    <div className={`toast toast-${type} ${isVisible ? 'visible' : 'hidden'}`}>
      <div className="toast-icon">{getIcon()}</div>
      <div className="toast-content">{message}</div>
      <button className="toast-close" onClick={handleClose} aria-label="Close notification">
        &times;
      </button>
    </div>
  );
};

// ToastContainer component to manage multiple toasts
const ToastContainer = () => {
  const [toasts, setToasts] = useState([]);
  
  // Function to add a new toast
  const addToast = (type, message, options = {}) => {
    const id = Date.now().toString();
    setToasts(prevToasts => [...prevToasts, { id, type, message, ...options }]);
    return id;
  };
  
  // Function to remove a toast by id
  const removeToast = (id) => {
    setToasts(prevToasts => prevToasts.filter(toast => toast.id !== id));
  };
  
  // Expose methods to the window object for global access
  useEffect(() => {
    window.toast = {
      info: (message, options) => addToast('info', message, options),
      success: (message, options) => addToast('success', message, options),
      warning: (message, options) => addToast('warning', message, options),
      error: (message, options) => addToast('error', message, options),
      remove: removeToast
    };
    
    return () => {
      delete window.toast;
    };
  }, []);
  
  if (toasts.length === 0) return null;
  
  return (
    <div className="toast-container">
      {toasts.map(toast => (
        <Toast
          key={toast.id}
          id={toast.id}
          type={toast.type}
          message={toast.message}
          onClose={removeToast}
          autoClose={toast.autoClose}
          duration={toast.duration}
        />
      ))}
    </div>
  );
};

export default ToastContainer;
