import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a request interceptor to include auth token
api.interceptors.request.use(
  (config) => {
    // Check for specific token types first
    const vendorToken = localStorage.getItem('vendorToken');
    const customerToken = localStorage.getItem('customerToken');

    if (vendorToken) {
      config.headers.Authorization = `Bearer ${vendorToken}`;
    } else if (customerToken) {
      config.headers.Authorization = `Bearer ${customerToken}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add a response interceptor to handle errors
api.interceptors.response.use(
  (response) => response.data,
  (error) => {
    // Handle token expiration
    if (error.response && error.response.status === 401) {
      localStorage.removeItem('vendorToken');
      localStorage.removeItem('customerToken');
      window.location.href = '/';
    }
    return Promise.reject(error);
  }
);

// Auth functions
export const registerVendor = (vendorData) => {
  return api.post('/vendors/register', vendorData);
};

export const loginVendor = async (credentials) => {
  const response = await api.post('/vendors/login', credentials);
  if (response.token) {
    localStorage.setItem('vendorToken', response.token);
  }
  return response;
};

export const registerCustomer = (customerData) => {
  return api.post('/customers/register', customerData);
};

export const loginCustomer = async (credentials) => {
  const response = await api.post('/customers/login', credentials);
  if (response.token) {
    localStorage.setItem('customerToken', response.token);
  }
  return response;
};

export const logout = () => {
  localStorage.removeItem('vendorToken');
  localStorage.removeItem('customerToken');
};

export const isVendorLoggedIn = () => {
  return !!localStorage.getItem('vendorToken');
};

export const isCustomerLoggedIn = () => {
  return !!localStorage.getItem('customerToken');
};

// Product functions
export const fetchProducts = (filters = {}) => {
  return api.get('/products', { params: filters });
};

export const fetchProductById = (id) => {
  return api.get(`/products/${id}`);
};

export const fetchVendorProducts = () => {
  return api.get('/vendors/products');
};

export const addProduct = (productData) => {
  return api.post('/products', productData);
};

export const updateProduct = (id, productData) => {
  return api.put(`/products/${id}`, productData);
};

export const deleteProduct = (id) => {
  return api.delete(`/products/${id}`);
};

// Cart functions
export const fetchCart = () => {
  return api.get('/cart');
};

export const addToCart = (productId, quantity) => {
  return api.post('/cart/items', { productId, quantity });
};

export const updateCartItem = (itemId, quantity) => {
  return api.put(`/cart/items/${itemId}`, { quantity });
};

export const removeFromCart = (itemId) => {
  return api.delete(`/cart/items/${itemId}`);
};

// Order functions
export const createOrder = (orderData) => {
  return api.post('/orders', orderData);
};

export const fetchOrders = () => {
  return api.get('/orders');
};

export const fetchOrderById = (id) => {
  return api.get(`/orders/${id}`);
};

export default api;
