import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { logout, isVendorLoggedIn, isCustomerLoggedIn } from '../../utils/api';
import { FiMenu, FiX, FiSearch, FiUser, FiLogOut, FiSettings, FiHome, FiShoppingCart, FiPackage, FiDollarSign } from 'react-icons/fi';
import '../../styles/theme.css';

const MainLayout = ({ children, userType = 'guest', pageTitle, showSearchBar = false, showContainer = true }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isVendor = userType === 'vendor' || isVendorLoggedIn();
  const isCustomer = userType === 'customer' || isCustomerLoggedIn();

  // State for dropdown menus
  const [accountDropdownOpen, setAccountDropdownOpen] = useState(false);
  const [userDropdownOpen, setUserDropdownOpen] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Refs for dropdown menus
  const accountDropdownRef = useRef(null);
  const userDropdownRef = useRef(null);
  const mobileMenuRef = useRef(null);

  // Close dropdowns when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (accountDropdownRef.current && !accountDropdownRef.current.contains(event.target)) {
        setAccountDropdownOpen(false);
      }
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setUserDropdownOpen(false);
      }
      if (mobileMenuRef.current && !mobileMenuRef.current.contains(event.target) && 
          event.target.className !== 'mobile-menu-toggle' && 
          event.target.parentNode.className !== 'mobile-menu-toggle') {
        setMobileMenuOpen(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Close dropdowns when changing routes
  useEffect(() => {
    setAccountDropdownOpen(false);
    setUserDropdownOpen(false);
    setMobileMenuOpen(false);
  }, [location.pathname]);

  const handleLogout = () => {
    if (isVendor) {
      logout('vendor');
      navigate('/vendor/login');
    } else if (isCustomer) {
      logout('customer');
      navigate('/vendi/login');
    } else {
      navigate('/');
    }
    setUserDropdownOpen(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const searchInput = e.target.elements.search.value;
    if (searchInput.trim()) {
      navigate(`/${isVendor ? 'vendor' : 'vendi'}/search?q=${encodeURIComponent(searchInput)}`);
    }
  };

  const toggleAccountDropdown = () => {
    setAccountDropdownOpen(!accountDropdownOpen);
    setUserDropdownOpen(false);
  };

  const toggleUserDropdown = () => {
    setUserDropdownOpen(!userDropdownOpen);
    setAccountDropdownOpen(false);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <header className="bg-white shadow-sm sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16 items-center">
            <Link to="/" className="flex items-center">
              <span className="text-2xl font-bold bg-gradient-to-r from-primary-500 to-primary-700 bg-clip-text text-transparent">
                MarketConnect
              </span>
            </Link>

            {showSearchBar && (
              <form className="flex-1 max-w-2xl px-6" onSubmit={handleSearch}>
                <div className="relative">
                  <input 
                    type="text" 
                    name="search" 
                    className="w-full pl-4 pr-10 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    placeholder="Search products..." 
                    aria-label="Search products"
                  />
                  <button 
                    type="submit" 
                    className="absolute right-0 top-0 h-full px-3 flex items-center justify-center text-gray-400 hover:text-gray-600"
                    aria-label="Search"
                  >
                    <FiSearch className="h-5 w-5" />
                  </button>
                </div>
              </form>
            )}

            <button 
              className="md:hidden p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-primary-500"
              onClick={toggleMobileMenu}
              aria-expanded={mobileMenuOpen}
              aria-label="Toggle menu"
            >
              {mobileMenuOpen ? (
                <FiX className="h-6 w-6" />
              ) : (
                <FiMenu className="h-6 w-6" />
              )}
            </button>

            <nav className={`${mobileMenuOpen ? 'block' : 'hidden'} md:block`} ref={mobileMenuRef}>
              <ul className="flex flex-col md:flex-row space-y-2 md:space-y-0 md:space-x-6">
                <li>
                  <Link 
                    to="/" 
                    className={`block py-2 px-3 rounded-md ${location.pathname === '/' ? 'text-primary-600 font-medium' : 'text-gray-700 hover:text-primary-600'}`}
                  >
                    Home
                  </Link>
                </li>
                {isVendor && (
                  <>
                    <li>
                      <Link 
                        to="/vendor/dashboard" 
                        className={`block py-2 px-3 rounded-md ${location.pathname.startsWith('/vendor/dashboard') ? 'text-primary-600 font-medium' : 'text-gray-700 hover:text-primary-600'}`}
                      >
                        Dashboard
                      </Link>
                    </li>
                    <li>
                      <Link 
                        to="/vendor/products" 
                        className={`block py-2 px-3 rounded-md ${location.pathname.startsWith('/vendor/products') ? 'text-primary-600 font-medium' : 'text-gray-700 hover:text-primary-600'}`}
                      >
                        Products
                      </Link>
                    </li>
                  </>
                )}
                {isCustomer && (
                  <>
                    <li>
                      <Link 
                        to="/vendi/products" 
                        className={`block py-2 px-3 rounded-md ${location.pathname.startsWith('/vendi/products') ? 'text-primary-600 font-medium' : 'text-gray-700 hover:text-primary-600'}`}
                      >
                        Products
                      </Link>
                    </li>
                    <li>
                      <Link 
                        to="/vendi/cart" 
                        className="flex items-center py-2 px-3 rounded-md text-gray-700 hover:text-primary-600"
                      >
                        <FiShoppingCart className="mr-2" />
                        Cart
                      </Link>
                    </li>
                  </>
                )}
              </ul>
            </nav>

            <div className="flex items-center space-x-4">
              {isVendor || isCustomer ? (
                <div className="relative" ref={userDropdownRef}>
                  <button 
                    onClick={toggleUserDropdown}
                    className="flex items-center justify-center h-10 w-10 rounded-full bg-primary-100 text-primary-700 font-medium focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
                    aria-expanded={userDropdownOpen}
                    aria-haspopup="true"
                  >
                    <span>{(isVendor ? 'V' : 'C').toUpperCase()}</span>
                  </button>

                  {userDropdownOpen && (
                    <div 
                      className="absolute right-0 mt-2 w-64 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 py-1 z-50"
                      role="menu"
                      aria-orientation="vertical"
                      aria-labelledby="user-menu"
                    >
                      <div className="px-4 py-3 border-b border-gray-100">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 h-10 w-10 rounded-full bg-primary-100 flex items-center justify-center text-primary-700 font-medium">
                            <FiUser className="h-5 w-5" />
                          </div>
                          <div className="ml-3">
                            <p className="text-sm font-medium text-gray-900">
                              {isVendor ? 'Vendor Account' : 'Customer Account'}
                            </p>
                            <p className="text-xs text-gray-500">
                              {isVendor ? 'Manage your vendor profile' : 'Manage your account'}
                            </p>
                          </div>
                        </div>
                      </div>
                      <div className="py-1">
                        <Link 
                          to={isVendor ? "/vendor/dashboard" : "/vendi/dashboard"}
                          className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          role="menuitem"
                        >
                          <FiHome className="mr-3 h-5 w-5 text-gray-400" />
                          Dashboard
                        </Link>
                        <Link 
                          to={isVendor ? "/vendor/products" : "/vendi/orders"}
                          className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          role="menuitem"
                        >
                          {isVendor ? (
                            <>
                              <FiPackage className="mr-3 h-5 w-5 text-gray-400" />
                              My Products
                            </>
                          ) : (
                            <>
                              <FiDollarSign className="mr-3 h-5 w-5 text-gray-400" />
                              My Orders
                            </>
                          )}
                        </Link>
                        <Link 
                          to={isVendor ? "/vendor/settings" : "/vendi/settings"}
                          className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          role="menuitem"
                        >
                          <FiSettings className="mr-3 h-5 w-5 text-gray-400" />
                          Settings
                        </Link>
                      </div>
                      <div className="border-t border-gray-100"></div>
                      <div className="py-1">
                        <button 
                          onClick={handleLogout}
                          className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-red-50 flex items-center"
                          role="menuitem"
                        >
                          <FiLogOut className="mr-3 h-5 w-5 text-red-400" />
                          Sign out
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              ) : (
                <>
                  <Link 
                    to={isVendor ? "/vendor/login" : "/vendi/login"} 
                    className="hidden md:inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-primary-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                  >
                    Log In
                  </Link>
                  <Link 
                    to={isVendor ? "/vendor/register" : "/vendi/register"} 
                    className="hidden md:inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                  >
                    Sign Up
                  </Link>
                </>
              )}

              <button 
                className="md:hidden p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-primary-500" 
                onClick={toggleMobileMenu}
                aria-label="Toggle menu"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>
          </div>
        </div>

        {/* Mobile menu */}
        {mobileMenuOpen && (
          <div className="md:hidden bg-white border-t border-gray-200">
            <div className="px-2 pt-2 pb-3 space-y-1">
              {isVendor ? (
                <>
                  <Link to="/vendor/dashboard" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Dashboard</Link>
                  <Link to="/vendor/products" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Products</Link>
                  <Link to="/vendor/orders" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Orders</Link>
                </>
              ) : isCustomer ? (
                <>
                  <Link to="/vendi" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Home</Link>
                  <Link to="/vendi/products" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Products</Link>
                  <Link to="/vendi/cart" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Cart</Link>
                </>
              ) : (
                <>
                  <Link to="/" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Home</Link>
                  <Link to="/vendi/products" className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-primary-600 hover:bg-gray-50">Browse Products</Link>
                  <Link to="/vendor/register" className="block px-3 py-2 rounded-md text-base font-medium text-primary-600 hover:bg-gray-50">Sell on MarketConnect</Link>
                </>
              )}

              {!isVendor && !isCustomer && (
                <div className="pt-4 pb-3 border-t border-gray-200">
                  <div className="space-y-1">
                    <Link 
                      to="/vendi/login" 
                      className="block w-full px-4 py-2 text-base font-medium text-center text-primary-700 hover:bg-gray-50 rounded-md"
                    >
                      Log In
                    </Link>
                    <Link 
                      to="/vendi/register" 
                      className="block w-full px-4 py-2 text-base font-medium text-center text-white bg-primary-600 hover:bg-primary-700 rounded-md"
                    >
                      Sign Up
                    </Link>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </header>

      <main className="main-content">
        {pageTitle && (
          <div className="page-header">
            <div className="container">
              <h1 className="page-title">{pageTitle}</h1>
            </div>
          </div>
        )}

        {showContainer ? (
          <div className="container">
            {children}
          </div>
        ) : (
          children
        )}
      </main>

      <footer className="main-footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>MarketConnect</h3>
              <p>Your one-stop shop for connecting vendors and customers in a seamless marketplace experience.</p>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/about">About Us</Link></li>
                <li><Link to="/contact">Contact</Link></li>
                <li><Link to="/privacy">Privacy Policy</Link></li>
                <li><Link to="/terms">Terms of Service</Link></li>
              </ul>
            </div>

            <div className="footer-section">
              <h3>Connect With Us</h3>
              <div className="social-links">
                <a href="#" aria-label="Facebook">Facebook</a>
                <a href="#" aria-label="Twitter">Twitter</a>
                <a href="#" aria-label="Instagram">Instagram</a>
                <a href="#" aria-label="LinkedIn">LinkedIn</a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Newsletter</h3>
              <form className="newsletter-form">
                <input 
                  type="email" 
                  placeholder="Your email address" 
                  aria-label="Your email address" 
                  required 
                />
                <button type="submit" aria-label="Subscribe">
                  Subscribe
                </button>
              </form>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; {new Date().getFullYear()} MarketConnect. All rights reserved.</p>
          </div>
        </div>
      </footer>
