import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import { Link } from 'react-router-dom';
import MainLayout from './layout/MainLayout';
import './Home.css';

// This component is the landing page of the MarketConnect marketplace platform
// It showcases both vendor and customer (Vendi) interfaces

function Home() {
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [trendingProducts, setTrendingProducts] = useState([]);
  const [flashDeals, setFlashDeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Auto-rotate testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Fetch trending products and flash deals
  useEffect(() => {
    const fetchHomeData = async () => {
      try {
        setLoading(true);
        setError(null);

        const [trendingRes, dealsRes] = await Promise.all([
          api.get('/products/trending'),
          api.get('/products/flash-deals')
        ]);

        if (trendingRes.data && dealsRes.data) {
          setTrendingProducts(trendingRes.data);
          setFlashDeals(dealsRes.data);
        } else {
          throw new Error('Invalid data format received from server');
        }
      } catch (err) {
        console.error('Home page data fetch error:', err);
        setError(
          err.response?.data?.error ||
          err.message ||
          'Failed to load home page data. Please try again later.'
        );
      } finally {
        setLoading(false);
      }
    };

    fetchHomeData();
  }, []);

  const features = [
    {
      id: 1,
      title: 'Discover Products',
      description: 'Browse thousands of quality products across multiple categories.',
      icon: '🛍️',
      link: '/vendi/all-products',
      color: 'var(--primary-color)'
    },
    {
      id: 2,
      title: 'Become a Vendor',
      description: 'Start selling your products and reach thousands of customers.',
      icon: '🏪',
      link: '/vendor/register',
      color: 'var(--accent-color)'
    },
    {
      id: 3,
      title: 'Secure Payments',
      description: 'Safe and secure payment options for all transactions.',
      icon: '🔒',
      link: '/about',
      color: 'var(--success)'
    },
    {
      id: 4,
      title: 'Fast Delivery',
      description: 'Get your products delivered quickly to your doorstep.',
      icon: '🚚',
      link: '/about',
      color: 'var(--warning)'
    }
  ];

  const categories = [
    { id: 1, name: 'Home & Garden', icon: '🏡', link: '/vendi/home-garden', color: '#4CAF50' },
    { id: 2, name: 'Beauty', icon: '💄', link: '/vendi/beauty', color: '#E91E63' },
    { id: 3, name: 'Food', icon: '🍔', link: '/vendi/food', color: '#FF9800' },
    { id: 4, name: 'Electronics', icon: '📱', link: '/vendi/electronics', color: '#2196F3' },
    { id: 5, name: 'Fashion', icon: '👗', link: '/vendi/fashion', color: '#9C27B0' },
    { id: 6, name: 'Health', icon: '💪', link: '/vendi/health', color: '#00BCD4' }
  ];

  const testimonials = [
    {
      id: 1,
      content: "MarketConnect has transformed my small business. I've reached customers I never thought possible!",
      name: "John Davis",
      role: "Vendor, Organic Foods",
      avatar: "👨‍💼"
    },
    {
      id: 2,
      content: "The variety of products and ease of shopping makes this my go-to marketplace for everything I need.",
      name: "Sarah Miller",
      role: "Customer",
      avatar: "👩‍💼"
    },
    {
      id: 3,
      content: "From registration to sales, the entire process is streamlined. I couldn't be happier with the results.",
      name: "Michael Chen",
      role: "Vendor, Kitchen Supplies",
      avatar: "👨‍🍳"
    }
  ];

  if (loading) {
    return (
      <MainLayout userType="guest" showContainer={false}>
        <div className="loading-spinner">Loading...</div>
      </MainLayout>
    );
  }

  if (error) {
    return (
      <MainLayout userType="guest" showContainer={false}>
        <div className="error-message">
          <span>{error}</span>
          <button onClick={() => window.location.reload()}>
            Try Again
          </button>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout userType="guest" showContainer={false}>
      <div className="home-container">
        <section className="hero-section">
          <div className="hero-content">
            <h1 className="hero-title">Welcome to MarketConnect</h1>
            <p className="hero-subtitle">The ultimate marketplace connecting vendors and customers</p>
            <div className="hero-buttons">
              <Link to="/vendi/login" className="btn btn-primary btn-lg">
                <span className="btn-icon">🛒</span> Shop Now
              </Link>
              <Link to="/vendor/register" className="btn btn-outline btn-lg">
                <span className="btn-icon">🏪</span> Become a Vendor
              </Link>
            </div>
          </div>
          <div className="hero-image">
            <div className="image-container">
              <div className="image-placeholder">
                <span className="hero-icon">🌐</span>
                <span className="marketplace-text">MarketConnect</span>
              </div>
            </div>
          </div>
        </section>

        {/* Flash Deals Section */}
        <section className="flash-deals-section">
          <h2 className="section-title">Flash Deals <span className="highlight">⚡</span></h2>
          <div className="deals-grid">
            {Array.isArray(flashDeals) && flashDeals.length > 0 ? flashDeals.map(deal => (
              <Link key={deal.product_id} to={`/vendi/product-details/${deal.product_id}`} className="deal-card">
                <div className="deal-image">
                  {deal.image ? (
                    <img 
                      src={deal.image} 
                      alt={deal.name} 
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/placeholder.png';
                      }}
                    />
                  ) : (
                    <div className="image-placeholder">🛍️</div>
                  )}
                  <span className="discount-badge">-{deal.discount}%</span>
                </div>
                <div className="deal-info">
                  <h3>{deal.name}</h3>
                  <div className="price-container">
                    <span className="original-price">${deal.originalPrice}</span>
                    <span className="deal-price">${deal.dealPrice}</span>
                  </div>
                  <div className="deal-timer">
                    <span className="timer-icon">⏰</span>
                    <span className="time-left">{deal.timeLeft}</span>
                  </div>
                </div>
              </Link>
            )) : (
              <div className="no-data-message">No flash deals available at the moment.</div>
            )}
          </div>
        </section>

        {/* Trending Products Section */}
        <section className="trending-section">
          <h2 className="section-title">Trending Now 🔥</h2>
          <div className="trending-grid">
            {Array.isArray(trendingProducts) && trendingProducts.length > 0 ? trendingProducts.map(product => (
              <Link key={product.product_id} to={`/vendi/product-details/${product.product_id}`} className="product-card">
                <div className="product-image">
                  {product.image ? (
                    <img 
                      src={product.image} 
                      alt={product.name} 
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/placeholder.png';
                      }}
                    />
                  ) : (
                    <div className="image-placeholder">🛍️</div>
                  )}
                  {product.rating >= 4.5 && <span className="trending-badge">🔥 Popular</span>}
                </div>
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p className="product-description">{product.description}</p>
                  <div className="product-meta">
                    <span className="product-price">${product.price}</span>
                    <span className="product-rating">⭐ {product.rating}</span>
                  </div>
                </div>
              </Link>
            )) : (
              <div className="no-data-message">No flash deals available at the moment.</div>
            )}
          </div>
        </section>

        <section className="features-section">
          <h2 className="section-title">Why Choose MarketConnect?</h2>
          <div className="features-grid">
            {features.map(feature => (
              <div key={feature.id} className="feature-card" style={{ '--card-accent': feature.color }}>
                <div className="feature-icon" style={{ color: feature.color }}>{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
                <Link to={feature.link} className="feature-link" style={{ color: feature.color }}>Learn More</Link>
              </div>
            ))}
          </div>
        </section>

        <section className="categories-section">
          <h2 className="section-title">Explore Categories</h2>
          <div className="categories-grid">
            {categories.map(category => (
              <Link key={category.id} to={category.link} className="category-card" style={{ '--category-color': category.color }}>
                <div className="category-icon">{category.icon}</div>
                <h3>{category.name}</h3>
              </Link>
            ))}
          </div>
        </section>

        <section className="cta-section">
          <div className="cta-content">
            <h2>Ready to start selling?</h2>
            <p>Join thousands of vendors already growing their business with MarketConnect.</p>
            <Link to="/vendor/register" className="btn btn-light btn-lg">
              <span className="btn-icon">🚀</span> Register as Vendor
            </Link>
          </div>
        </section>

        <section className="testimonials-section">
          <h2 className="section-title">What Our Users Say</h2>
          <div className="testimonials-slider">
            {testimonials.map((testimonial, index) => (
              <div
                key={testimonial.id}
                className={`testimonial-card ${index === activeTestimonial ? 'active' : ''}`}
                style={{ transform: `translateX(${(index - activeTestimonial) * 100}%)` }}
              >
                <div className="testimonial-content">
                  <p>"{testimonial.content}"</p>
                </div>
                <div className="testimonial-author">
                  <div className="author-avatar">{testimonial.avatar}</div>
                  <div className="author-info">
                    <h4>{testimonial.name}</h4>
                    <p>{testimonial.role}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="testimonial-indicators">
            {testimonials.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === activeTestimonial ? 'active' : ''}`}
                onClick={() => setActiveTestimonial(index)}
                aria-label={`View testimonial ${index + 1}`}
              />
            ))}
          </div>
        </section>

        <section className="newsletter-section">
          <div className="newsletter-container">
            <div className="newsletter-content">
              <h2>Subscribe to our Newsletter</h2>
              <p>Stay updated with new products, promotions, and marketplace news.</p>
              <form className="newsletter-form" onSubmit={(e) => {
                e.preventDefault();
                // Add newsletter subscription logic here
                alert('Thank you for subscribing!');
              }}>
                <div className="form-group">
                  <input
                    type="email"
                    placeholder="Enter your email address"
                    required
                  />
                  <button type="submit" className="btn btn-primary">
                    Subscribe <span className="btn-icon">✉️</span>
                  </button>
                </div>
                <div className="form-check">
                  <input type="checkbox" id="newsletter-consent" required />
                  <label htmlFor="newsletter-consent">
                    I agree to receive marketing emails from MarketConnect
                  </label>
                </div>
              </form>
            </div>
          </div>
        </section>
      </div>
    </MainLayout>
  );
}

export default Home;
