import React, { useState } from 'react';
import './ReturnRequest.css'; // Ensure you have a corresponding CSS file for styling

const ReturnRequest = () => {
  const [selectedItems, setSelectedItems] = useState([]);
  const [returnReason, setReturnReason] = useState('');
  const [returnMethod, setReturnMethod] = useState('');
  const [additionalDetails, setAdditionalDetails] = useState('');

  const handleItemSelect = (itemName, price, quantity) => {
    const item = { name: itemName, price, quantity };
    setSelectedItems((prevItems) =>
      prevItems.some((i) => i.name === itemName)
        ? prevItems.map((i) => (i.name === itemName ? item : i))
        : [...prevItems, item]
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Return Request Submitted:', {
      items: selectedItems,
      reason: returnReason,
      method: returnMethod,
      details: additionalDetails,
    });
  };

  const sampleItems = [
    { name: 'Product Name 1', price: 19.99, quantity: 2 },
    { name: 'Product Name 2', price: 29.99, quantity: 1 },
  ];

  const total = selectedItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const shippingFee = 5.0;
  const restockingFee = 3.0;
  const estimatedRefund = total - shippingFee - restockingFee;

  return (
    <div className="return-request-container">
      <h1>Return Request</h1>
      <p>Date: Oct 2023-10-08</p>
      <p>Return Policy: Items can be returned within 30 days of delivery. Shipping costs for returns may apply based on the reason for return.</p>

      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <h2>Select Items to Return</h2>
          {sampleItems.map((item, index) => (
            <div key={index} className="item-selection">
              <input
                type="checkbox"
                id={`item-${index}`}
                onChange={() => handleItemSelect(item.name, item.price, item.quantity)}
              />
              <label htmlFor={`item-${index}`}>
                {item.name} - ${item.price.toFixed(2)} Ã— {item.quantity}
              </label>
            </div>
          ))}
        </div>

        <div className="form-section">
          <h2>Return Reason</h2>
          <select value={returnReason} onChange={(e) => setReturnReason(e.target.value)} required>
            <option value="">Select reason for return</option>
            <option value="Defective">Defective</option>
            <option value="Not as described">Not as described</option>
            <option value="Changed mind">Changed mind</option>
          </select>
          <textarea
            placeholder="Additional details"
            value={additionalDetails}
            onChange={(e) => setAdditionalDetails(e.target.value)}
            rows="4"
          ></textarea>
        </div>

        <div className="form-section">
          <h2>Return Method</h2>
          <div className="return-method">
            <label>
              <input
                type="radio"
                name="returnMethod"
                value="Ship Back"
                checked={returnMethod === 'Ship Back'}
                onChange={() => setReturnMethod('Ship Back')}
              />
              Ship Back
            </label>
            <label>
              <input
                type="radio"
                name="returnMethod"
                value="Drop-off"
                checked={returnMethod === 'Drop-off'}
                onChange={() => setReturnMethod('Drop-off')}
              />
              Drop-off
            </label>
          </div>
        </div>

        <div className="form-section">
          <h2>Return Summary</h2>
          <div className="summary-row">
            <p>Item Total</p>
            <p>${total.toFixed(2)}</p>
          </div>
          <div className="summary-row">
            <p>Return Shipping Fee</p>
            <p>${shippingFee.toFixed(2)}</p>
          </div>
          <div className="summary-row">
            <p>Restocking Fee</p>
            <p>${restockingFee.toFixed(2)}</p>
          </div>
          <div className="summary-row total">
            <p>Estimated Refund</p>
            <p>${estimatedRefund.toFixed(2)}</p>
          </div>
        </div>

        <button type="submit" className="submit-btn">Submit Return Request</button>
      </form>
    </div>
  );
};

export default ReturnRequest;
