import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import MainLayout from '../layout/MainLayout';
import './VendiLogin.css';
import axios from 'axios';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import GoogleIcon from '@mui/icons-material/Google';
import FacebookIcon from '@mui/icons-material/Facebook';

const VendiLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [success, setSuccess] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if we have saved credentials in localStorage
    const savedEmail = localStorage.getItem('savedCustomerEmail');
    if (savedEmail) {
      setEmail(savedEmail);
      setRememberMe(true);
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/customers/login', {
        email,
        password,
      });

      if (response.data && response.data.token) {
        // Store token and customer info
        localStorage.setItem('customerToken', response.data.token);
        localStorage.setItem('customerInfo', JSON.stringify(response.data.customer));

        // Save email if remember me is checked
        if (rememberMe) {
          localStorage.setItem('savedCustomerEmail', email);
        } else {
          localStorage.removeItem('savedCustomerEmail');
        }

        // Show success message
        setSuccess('Login successful! Redirecting...');

        // Redirect after a short delay
        setTimeout(() => {
          navigate('/vendi/dashboard');
        }, 1500);
      } else {
        setError(response.data.error || 'Invalid email or password');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError('An error occurred. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleSocialLogin = (platform) => {
    setError(`${platform} login is not implemented yet. Please use email login.`);
  };

  return (
    <MainLayout userType="guest" pageTitle="Customer Login">
      <div className="login-container">
        <div className="login-card">
          <h2 className="login-title">Welcome Back!</h2>
          <p className="login-subtitle">Sign in to access your customer account</p>

          <div className="social-login">
            <button 
              type="button" 
              aria-label="Sign in with Google"
              onClick={() => handleSocialLogin('Google')}
            >
              <GoogleIcon size={18} />
              Google
            </button>
            <button 
              type="button" 
              aria-label="Sign in with Facebook"
              onClick={() => handleSocialLogin('Facebook')}
            >
              <FacebookIcon size={18} />
              Facebook
            </button>
          </div>

          <div className="separator">or sign in with email</div>

          {error && <div className="form-error">{error}</div>}
          {success && <div className="form-success">{success}</div>}

          <form onSubmit={handleLogin} className="login-form">
            <div className="form-group">
              <input
                type="email"
                className="form-control"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                placeholder="Enter your email"
                id="email"
              />
              <label className="form-label" htmlFor="email">Email</label>
            </div>
            <div className="form-group">
              <div className="password-input">
                <input
                  type={showPassword ? "text" : "password"}
                  className="form-control"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder="Enter your password"
                  id="password"
                />
                <button
                  type="button"
                  className="toggle-password"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? "Hide password" : "Show password"}
                >
                  {showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}
                </button>
              </div>
              <label className="form-label" htmlFor="password">Password</label>
            </div>
            <div className="flex-row justify-between items-center mb-md">
              <div className="remember-me">
                <input 
                  type="checkbox" 
                  id="remember" 
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                />
                <label htmlFor="remember">Remember Me</label>
              </div>
              <Link to="/vendi/forgot-password" className="forgot-password">
                Forgot Password?
              </Link>
            </div>
            <button 
              type="submit" 
              className={`btn btn-primary btn-block ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? 'Signing In...' : 'Sign In'}
            </button>
          </form>

          <div className="login-footer">
            <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
          </div>
        </div>
      </div>
    </MainLayout>
  );
};

export default VendiLogin;
