import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { vendorAPI } from '../../utils/api';
import './VendorRegister.css';

// Validation helpers
const validateEmail = (email) => {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
};

const validatePhone = (phone) => {
  return /^\+?[1-9]\d{9,14}$/.test(phone);
};

const validatePassword = (password) => {
  return {
    isValid: password.length >= 8 && /[A-Z]/.test(password) && /[a-z]/.test(password) && /[0-9]/.test(password),
    message: password.length < 8 ? 'Password must be at least 8 characters' :
             !/[A-Z]/.test(password) ? 'Password must contain an uppercase letter' :
             !/[a-z]/.test(password) ? 'Password must contain a lowercase letter' :
             !/[0-9]/.test(password) ? 'Password must contain a number' : ''
  };
};

const VendorRegister = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    businessName: '',
    businessType: '',
    businessAddress: '',
    registrationNumber: '',
    contactPerson: '',
    email: '',
    phone: '',
    username: '',
    password: '',
    confirmPassword: '',
    logo: null,
  });

  // Load saved form data on mount
  useEffect(() => {
    const savedData = localStorage.getItem('vendorRegistrationDraft');
    if (savedData) {
      try {
        setFormData(JSON.parse(savedData));
      } catch (error) {
        console.error('Error loading saved form data:', error);
      }
    }
  }, []);

  // Save form data on change
  const handleChange = (e) => {
    const { name, value } = e.target;
    const newFormData = { ...formData, [name]: value };
    setFormData(newFormData);

    try {
      localStorage.setItem('vendorRegistrationDraft', JSON.stringify(newFormData));
    } catch (error) {
      console.error('Error saving form data:', error);
    }

    // Clear specific field error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }

    // Validate fields on change
    if (name === 'email' && value) {
      if (!validateEmail(value)) {
        setErrors(prev => ({ ...prev, email: 'Please enter a valid email address' }));
      }
    } else if (name === 'phone' && value) {
      if (!validatePhone(value)) {
        setErrors(prev => ({ ...prev, phone: 'Please enter a valid phone number' }));
      }
    } else if (name === 'password' && value) {
      const { isValid, message } = validatePassword(value);
      if (!isValid) {
        setErrors(prev => ({ ...prev, password: message }));
      }
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.businessName.trim()) newErrors.businessName = 'Business name is required';
    if (!formData.businessType) newErrors.businessType = 'Business type is required';
    if (!formData.businessAddress.trim()) newErrors.businessAddress = 'Business address is required';
    if (!formData.registrationNumber.trim()) newErrors.registrationNumber = 'Registration number is required';
    if (!formData.contactPerson.trim()) newErrors.contactPerson = 'Contact person is required';

    if (!validateEmail(formData.email)) newErrors.email = 'Please enter a valid email address';
    if (!validatePhone(formData.phone)) newErrors.phone = 'Please enter a valid phone number';

    const { isValid, message } = validatePassword(formData.password);
    if (!isValid) newErrors.password = message;

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please fix the errors in the form');
      return;
    }

    setIsLoading(true);

    // Remove confirmPassword from data sent to API
    const { confirmPassword, ...vendorData } = formData;

    try {
      const response = await vendorAPI.register(vendorData);
      if (response.data) {
        toast.success('Registration successful!');
        // Clear saved form data
        localStorage.removeItem('vendorRegistrationDraft');

        // Store token and vendor info in localStorage
        localStorage.setItem('vendorToken', response.data.token);
        localStorage.setItem('vendorInfo', JSON.stringify(response.data.vendor));

        // Display the QR code ID card
        if (response.data.vendor && response.data.vendor.qr_code) {
          // Navigate to a page that displays the QR code or ID card
          // For now, we'll redirect to dashboard and can implement ID display later
          window.location.href = '/vendor/dashboard';
        } else {
          window.location.href = '/vendor/dashboard';
        }
      } else {
        toast.error('Registration failed: ' + response.data.error);
      }
    } catch (error) {
      console.error('Error during registration:', error);
      toast.error('Unable to connect to server. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="vendor-register-container">
      <nav className="navbar">
        <div className="logo">MarketConnect</div>
        <ul className="nav-links">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/contact">Contact</Link></li>
        </ul>
      </nav>

      <div className="register-header">
        <h1>Register as a Vendor</h1>
        <p>Complete the form below to create your vendor account and receive your digital ID card</p>
      </div>

      <ToastContainer position="top-right" autoClose={5000} />
      <div className="register-form">
        {/* Toast container for notifications */}
        <ToastContainer position="top-right" autoClose={5000} />
        <form onSubmit={handleSubmit}>
          <h2>Business Information</h2>
          <div className="form-group">
            <label htmlFor="businessName">Business Name</label>
            <input
              type="text"
              id="businessName"
              name="businessName"
              placeholder="Enter your business name"
              value={formData.businessName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="businessType">Business Type</label>
            <select
              id="businessType"
              name="businessType"
              value={formData.businessType}
              onChange={handleChange}
              required
            >
              <option value="">Select business type</option>
              <option value="retail">Retail</option>
              <option value="wholesale">Wholesale</option>
              <option value="service">Service</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="businessAddress">Business Address</label>
            <input
              type="text"
              id="businessAddress"
              name="businessAddress"
              placeholder="Enter your business address"
              value={formData.businessAddress}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="registrationNumber">Business Registration Number</label>
            <input
              type="text"
              id="registrationNumber"
              name="registrationNumber"
              placeholder="Enter registration number"
              value={formData.registrationNumber}
              onChange={handleChange}
              required
            />
          </div>

          <h2>Contact Information</h2>
          <div className="form-group">
            <label htmlFor="contactPerson">Contact Person Name</label>
            <input
              type="text"
              id="contactPerson"
              name="contactPerson"
              placeholder="Enter full name"
              value={formData.contactPerson}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email address"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="phone">Phone Number</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              placeholder="Enter phone number"
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </div>

          <h2>Account Credentials</h2>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              placeholder="Create a username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Create a password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              placeholder="Confirm your password"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          </div>

          <h2>Business Logo (Optional)</h2>
          <div className="form-group">
            <label htmlFor="logo">Upload Logo</label>
            <input
              type="file"
              id="logo"
              name="logo"
              onChange={(e) => setFormData({ ...formData, logo: e.target.files[0] })}
            />
          </div>

          <div className="form-group terms">
            <input type="checkbox" id="terms" required />
            <label htmlFor="terms">I agree to the Terms and Conditions of MarketConnect</label>
          </div>

          <button type="submit" className="register-button" disabled={isLoading}>
            {isLoading ? (
              <>
                Registering...
                <div className="loading-spinner" />
              </>
            ) : (
              'Register'
            )}
          </button>
        </form>
        <div className="login-link">
          <p>Already have an account?</p>
          <Link to="/vendor/login" className="login-link-button">Login</Link>
        </div>
      </div>
    </div>
  );
};

export default VendorRegister;
