import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { logout, isVendorLoggedIn, isCustomerLoggedIn } from '../../utils/api';
import './MainLayout.css';

const MainLayout = ({ children, userType = 'guest', pageTitle, showSearchBar = false, showContainer = true }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isVendor = userType === 'vendor' || isVendorLoggedIn();
  const isCustomer = userType === 'customer' || isCustomerLoggedIn();

  // State for dropdown menus
  const [accountDropdownOpen, setAccountDropdownOpen] = useState(false);
  const [userDropdownOpen, setUserDropdownOpen] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Refs for dropdown menus
  const accountDropdownRef = useRef(null);
  const userDropdownRef = useRef(null);
  const mobileMenuRef = useRef(null);

  // Close dropdowns when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (accountDropdownRef.current && !accountDropdownRef.current.contains(event.target)) {
        setAccountDropdownOpen(false);
      }
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setUserDropdownOpen(false);
      }
      if (mobileMenuRef.current && !mobileMenuRef.current.contains(event.target) && 
          event.target.className !== 'mobile-menu-toggle' && 
          event.target.parentNode.className !== 'mobile-menu-toggle') {
        setMobileMenuOpen(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Close dropdowns when changing routes
  useEffect(() => {
    setAccountDropdownOpen(false);
    setUserDropdownOpen(false);
    setMobileMenuOpen(false);
  }, [location.pathname]);

  const handleLogout = () => {
    if (isVendor) {
      logout('vendor');
      navigate('/vendor/login');
    } else if (isCustomer) {
      logout('customer');
      navigate('/vendi/login');
    } else {
      navigate('/');
    }
    setUserDropdownOpen(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const searchInput = e.target.elements.search.value;
    if (searchInput.trim()) {
      navigate(`/${isVendor ? 'vendor' : 'vendi'}/search?q=${encodeURIComponent(searchInput)}`);
    }
  };

  const toggleAccountDropdown = () => {
    setAccountDropdownOpen(!accountDropdownOpen);
    setUserDropdownOpen(false);
  };

  const toggleUserDropdown = () => {
    setUserDropdownOpen(!userDropdownOpen);
    setAccountDropdownOpen(false);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  return (
    <div className="main-layout">
      <header className="main-header">
        <div className="container">
          <div className="header-content">
            <Link to="/" className="logo">
              <span className="logo-text">MarketConnect</span>
            </Link>

            {showSearchBar && (
              <form className="search-bar" onSubmit={handleSearch}>
                <input 
                  type="text" 
                  name="search" 
                  placeholder="Search products..." 
                  aria-label="Search products"
                />
                <button type="submit" aria-label="Search">
                  <i className="search-icon">🔍</i>
                </button>
              </form>
            )}

            <button className="mobile-menu-toggle" onClick={toggleMobileMenu} aria-label="Toggle menu">
              <span></span>
              <span></span>
              <span></span>
            </button>

            <nav className={`main-nav ${mobileMenuOpen ? 'mobile-open' : ''}`} ref={mobileMenuRef}>
              <ul className="nav-links">
                <li className={location.pathname === '/' ? 'active' : ''}>
                  <Link to="/">Home</Link>
                </li>
                {isVendor ? (
                  <>
                    <li className={location.pathname === '/vendor/dashboard' ? 'active' : ''}>
                      <Link to="/vendor/dashboard">Dashboard</Link>
                    </li>
                    <li className={location.pathname === '/vendor/product-inventory' ? 'active' : ''}>
                      <Link to="/vendor/product-inventory">Products</Link>
                    </li>
                    <li className={location.pathname === '/vendor/orders' ? 'active' : ''}>
                      <Link to="/vendor/orders">Orders</Link>
                    </li>
                  </>
                ) : isCustomer ? (
                  <>
                    <li className={location.pathname === '/vendi/all-products' ? 'active' : ''}>
                      <Link to="/vendi/all-products">Products</Link>
                    </li>
                    <li className={location.pathname === '/vendi/dashboard' ? 'active' : ''}>
                      <Link to="/vendi/dashboard">Dashboard</Link>
                    </li>
                    <li className={location.pathname.includes('/vendi/cart') ? 'active' : ''}>
                      <Link to="/vendi/cart">Cart</Link>
                    </li>
                  </>
                ) : (
                  <>
                    <li className={location.pathname === '/about' ? 'active' : ''}>
                      <Link to="/about">About</Link>
                    </li>
                    <li className={location.pathname === '/contact' ? 'active' : ''}>
                      <Link to="/contact">Contact</Link>
                    </li>
                    <li className={`dropdown ${accountDropdownOpen ? 'open' : ''}`} ref={accountDropdownRef}>
                      <button 
                        className="dropdown-toggle" 
                        onClick={toggleAccountDropdown}
                        aria-expanded={accountDropdownOpen}
                        aria-haspopup="true"
                      >
                        Account
                      </button>
                      <div className={`dropdown-menu ${accountDropdownOpen ? 'show' : ''}`}>
                        <Link to="/vendor/login">Vendor Login</Link>
                        <Link to="/vendor/register">Vendor Register</Link>
                        <Link to="/vendi/login">Customer Login</Link>
                        <Link to="/vendi/signup">Customer Register</Link>
                      </div>
                    </li>
                  </>
                )}
              </ul>
            </nav>

            {(isVendor || isCustomer) && (
              <div className={`user-actions ${userDropdownOpen ? 'open' : ''}`} ref={userDropdownRef}>
                <button 
                  className="user-profile" 
                  onClick={toggleUserDropdown}
                  aria-expanded={userDropdownOpen}
                  aria-haspopup="true"
                >
                  <span className="profile-icon">👤</span>
                </button>
                <div className={`dropdown-menu ${userDropdownOpen ? 'show' : ''}`}>
                  <Link to={`/${isVendor ? 'vendor' : 'vendi'}/profile-settings`}>
                    <i className="icon-profile">👤</i> Profile
                  </Link>
                  <Link to={`/${isVendor ? 'vendor' : 'vendi'}/dashboard`}>
                    <i className="icon-dashboard">📊</i> Dashboard
                  </Link>
                  {isCustomer && (
                    <Link to="/vendi/cart">
                      <i className="icon-cart">🛒</i> Cart
                    </Link>
                  )}
                  <button onClick={handleLogout} className="logout-btn">
                    <i className="icon-logout">🚪</i> Logout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      <main className="main-content">
        {pageTitle && (
          <div className="page-header">
            <div className="container">
              <h1 className="page-title">{pageTitle}</h1>
            </div>
          </div>
        )}

        {showContainer ? (
          <div className="container">
            {children}
          </div>
        ) : (
          children
        )}
      </main>

      <footer className="main-footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>MarketConnect</h3>
              <p>Your one-stop shop for connecting vendors and customers in a seamless marketplace experience.</p>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/about">About Us</Link></li>
                <li><Link to="/contact">Contact</Link></li>
                <li><Link to="/privacy">Privacy Policy</Link></li>
                <li><Link to="/terms">Terms of Service</Link></li>
              </ul>
            </div>

            <div className="footer-section">
              <h3>Connect With Us</h3>
              <div className="social-links">
                <a href="#" aria-label="Facebook">Facebook</a>
                <a href="#" aria-label="Twitter">Twitter</a>
                <a href="#" aria-label="Instagram">Instagram</a>
                <a href="#" aria-label="LinkedIn">LinkedIn</a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Newsletter</h3>
              <form className="newsletter-form">
                <input 
                  type="email" 
                  placeholder="Your email address" 
                  aria-label="Your email address" 
                  required 
                />
                <button type="submit" aria-label="Subscribe">
                  Subscribe
                </button>
              </form>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; {new Date().getFullYear()} MarketConnect. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default MainLayout;
