import React, { useState } from "react";
import './VendiSignup.css';
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import MainLayout from '../layout/MainLayout';

const SignupPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!name || !email || !password || !confirmPassword) {
      return setError("Please fill in all fields.");
    }

    if (password.length < 6) {
      return setError("Password must be at least 6 characters long.");
    }

    if (password !== confirmPassword) {
      return setError("Passwords do not match.");
    }

    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      return setError("Please enter a valid email address.");
    }

    try {
      console.log('Sending registration request...');
      const response = await axios.post("http://localhost:5000/api/customers/register", {
        name,
        email,
        password,
        phone: '', // Optional fields in our schema
        address: ''
      }, {
        headers: {
          'Content-Type': 'application/json'
        },
        withCredentials: true
      });

      console.log('Registration response:', response);

      if (response.status === 201 && response.data.token) {
        // Store token and customer info
        localStorage.setItem('customerToken', response.data.token);
        localStorage.setItem('customerInfo', JSON.stringify(response.data.customer));
        console.log('Registration successful, navigating to dashboard...');

        // Show success message
        alert(response.data.message || "Registration successful! You are now logged in.");

        // Redirect to dashboard
        navigate("/vendi/dashboard");
      } else {
        const errorMsg = response.data?.error || "Registration failed. Please try again.";
        console.error('Registration failed:', errorMsg);
        setError(errorMsg);
      }
    } catch (err) {
      console.error("Registration error:", err);

      if (err.response) {
        // The request was made and the server responded with a status code
        console.error('Error response data:', err.response.data);
        console.error('Error status:', err.response.status);

        if (err.response.data && err.response.data.error) {
          setError(err.response.data.error);
        } else if (err.response.status === 409) {
          setError("This email is already registered. Please use a different email or log in.");
        } else if (err.response.status === 400) {
          setError("Invalid request. Please check your input and try again.");
        } else {
          setError("An error occurred during registration. Please try again later.");
        }
      } else if (err.request) {
        // The request was made but no response was received
        console.error('No response received:', err.request);
        setError("Unable to connect to the server. Please check your internet connection and try again.");
      } else {
        // Something happened in setting up the request
        console.error('Error:', err.message);
        setError("An error occurred. Please try again.");
      }
    }
  };

  return (
    <MainLayout userType="guest" pageTitle="Create Customer Account">
      <div className="signup-container">
        <div className="card card-padding-xl">
          <h2 className="login-title mb-md">Join MarketConnect</h2>
          <p className="login-subtitle mb-xl">Create your account to discover amazing products</p>

          <div className="social-login">
            <button type="button" aria-label="Sign up with Google">
              <img src="/images/google.svg" alt="Google" width="24" height="24" />
              Google
            </button>
            <button type="button" aria-label="Sign up with Facebook">
              <img src="/images/facebook.svg" alt="Facebook" width="24" height="24" />
              Facebook
            </button>
          </div>

          <div className="separator">or sign up with email</div>

          {error && <div className="form-error mb-md">{error}</div>}

          <form onSubmit={handleSubmit} className="text-left">
            <div className="form-group">
              <label className="form-label" htmlFor="name">Full Name</label>
              <input
                className="form-control"
                id="name"
                type="text"
                placeholder="Your full name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label" htmlFor="email">Email Address</label>
              <input
                className="form-control"
                id="email"
                type="email"
                placeholder="Your email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label" htmlFor="password">Password</label>
              <input
                className="form-control"
                id="password"
                type="password"
                placeholder="Create a password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <div className="form-group mb-lg">
              <label className="form-label" htmlFor="confirmPassword">Confirm Password</label>
              <input
                className="form-control"
                id="confirmPassword"
                type="password"
                placeholder="Confirm your password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>

            <div className="form-group mb-md">
              <div className="flex-row items-center">
                <input type="checkbox" id="terms" className="mr-sm" required />
                <label htmlFor="terms">I agree to the <Link to="/terms" className="text-primary">Terms of Service</Link> and <Link to="/privacy" className="text-primary">Privacy Policy</Link></label>
              </div>
            </div>

            <button
              className="btn btn-primary btn-block mb-lg"
              type="submit"
            >
              Create Account
            </button>
          </form>

          <div className="login-footer">
            <p>Already have an account? <Link to="/vendi/login">Sign In</Link></p>
          </div>
        </div>
      </div>
    </MainLayout>
  );
};

export default SignupPage;
