import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './VendorDashboard.css';
import { vendorAPI, logout } from '../../utils/api';

const VendorDashboard = () => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Check if vendor is logged in
    const vendorInfo = localStorage.getItem('vendorInfo');
    const vendorToken = localStorage.getItem('vendorToken');

    if (!vendorInfo || !vendorToken) {
      navigate('/vendor/login');
      return;
    }

    // Load vendor profile, products, and orders
    const fetchDashboardData = async () => {
      try {
        setLoading(true);

        // Get vendor profile
        const profileResponse = await vendorAPI.getProfile();
        setVendor(profileResponse.data.vendor);

        // Get vendor products
        const productsResponse = await vendorAPI.getProducts();
        setProducts(productsResponse.data.products || []);

        // Get vendor orders
        const ordersResponse = await vendorAPI.getOrders();
        setOrders(ordersResponse.data.orders || []);

        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        if (err.response && err.response.status === 401) {
          logout('vendor');
          navigate('/vendor/login');
        }
        setError('Failed to load dashboard data');
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate]);

  // Handle logout
  const handleLogout = () => {
    logout('vendor');
    navigate('/vendor/login');
  };

  // Redirect to product management page
  const handleAddProduct = () => {
    navigate('/vendor/add-product');
  };

  // Redirect to profile update page
  const handleUpdateProfile = () => {
    navigate('/vendor/profile-update');
  };

  // Calculate total revenue
  const calculateTotalRevenue = () => {
    if (!orders || orders.length === 0) return 0;
    return orders.reduce((total, order) => total + parseFloat(order.total_amount), 0).toFixed(2);
  };

  if (loading) {
    return (
      <div className="dashboard-container loading">
        <h2>Loading dashboard...</h2>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {vendor ? vendor.business_name : 'Vendor'}</h2>
        <div className="header-actions">
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      </header>

      <nav className="breadcrumb">Home &gt; Vendor &gt; Dashboard</nav>

      {error && <div className="error-message">{error}</div>}

      <section className="account-status">
        <h4>Account Status</h4>
        <div className="status-box">
          <p className="status-title">ACTIVE</p>
          <h5>Your vendor account is active</h5>
          <p>You can now add products and manage your store.</p>
          <button className="primary-btn" onClick={handleUpdateProfile}>Complete Profile</button>
        </div>
      </section>

      <section className="quick-actions">
        <h4>Quick Actions</h4>
        <div className="action-grid">
          <div className="action-card">
            <h5>Add Products</h5>
            <p>Start listing your products in the marketplace.</p>
            <button onClick={handleAddProduct}>Add Now</button>
          </div>
          <div className="action-card">
            <h5>Manage Inventory</h5>
            <p>View and update your product inventory.</p>
            <Link to="/vendor/product-inventory"><button>Manage</button></Link>
          </div>
          <div className="action-card">
            <h5>View ID Card</h5>
            <p>Access your vendor identification card with QR code.</p>
            <Link to="/vendor/id-card"><button>View</button></Link>
          </div>
        </div>
      </section>

      <section className="performance-overview">
        <h4>Performance Overview</h4>
        <div className="overview-grid">
          <div className="overview-card">
            <p>{products.length}</p>
            <h6>Products</h6>
            <Link to="/vendor/product-inventory"><button>View Details</button></Link>
          </div>
          <div className="overview-card">
            <p>{orders.length}</p>
            <h6>Orders</h6>
            <Link to="/vendor/orders"><button>View Details</button></Link>
          </div>
          <div className="overview-card">
            <p>${calculateTotalRevenue()}</p>
            <h6>Revenue</h6>
            <Link to="/vendor/sales"><button>View Details</button></Link>
          </div>
          <div className="overview-card">
            <p>{products.reduce((sum, product) => sum + product.stock_quantity, 0)}</p>
            <h6>Total Inventory</h6>
            <Link to="/vendor/inventory"><button>View Details</button></Link>
          </div>
        </div>
      </section>

      <section className="getting-started">
        <h4>Resources</h4>
        <ul>
          <li>
            <Link to="/vendor/guide"><span>📘</span> Vendor Guide <span className="arrow">→</span></Link>
          </li>
          <li>
            <Link to="/vendor/photography-tips"><span>📷</span> Product Photography Tips <span className="arrow">→</span></Link>
          </li>
          <li>
            <Link to="/vendor/pricing-strategies"><span>💸</span> Pricing Strategies <span className="arrow">→</span></Link>
          </li>
        </ul>
      </section>

      <section className="recent-orders">
        <h4>Recent Orders</h4>
        {orders.length === 0 ? (
          <p>No orders yet. Start adding products to your inventory.</p>
        ) : (
          <table className="orders-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Date</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {orders.slice(0, 5).map(order => (
                <tr key={order.order_id}>
                  <td>{order.order_id}</td>
                  <td>{new Date(order.order_date).toLocaleDateString()}</td>
                  <td>${parseFloat(order.total_amount).toFixed(2)}</td>
                  <td>
                    <span className={`status-badge status-${order.status.toLowerCase()}`}>
                      {order.status}
                    </span>
                  </td>
                  <td>
                    <Link to={`/vendor/orders/${order.order_id}`}>
                      <button className="view-btn">View</button>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default VendorDashboard;
