import React from 'react';
import './ClothingCollection.css'; // Ensure you have a corresponding CSS file for styling

const ClothingCollection = () => {
  const featuredClothing = [
    { name: 'Premium T-Shirt', price: 29.99 },
    { name: 'Slim Fit Jeans', price: 49.99 },
    { name: 'Summer Dress', price: 39.99 },
    { name: 'Casual Jacket', price: 59.99 },
  ];

  const newArrivals = [
    { name: 'Designer Sneakers', price: 89.99, image: 'path-to-sneakers.jpg' },
    { name: 'Leather Jacket', price: 129.99, image: 'path-to-jacket.jpg' },
  ];

  const shopByCategory = [
    { name: 'Tops & T-shirts', subcategories: ['Graphic Tees', 'Polos', 'Tank Tops'] },
    { name: 'Pants & Jeans', subcategories: ['Skinny Jeans', 'Chinos', 'Cargo Pants'] },
    { name: 'Dresses & Skirts', subcategories: ['Casual Dresses', 'Formal Dresses', 'Midi Skirts'] },
    { name: 'Jackets & Coats', subcategories: ['Denim Jackets', 'Trench Coats', 'Leather Jackets'] },
  ];

  const popularBrands = [
    { name: 'Urban Style', image: 'path-to-urban.jpg' },
    { name: 'Classic Fit', image: 'path-to-classic.jpg' },
    { name: 'Eco Apparel', image: 'path-to-eco.jpg' },
  ];

  return (
    <div className="clothing-collection-container">
      <header className="clothing-collection-header">
        <h1>Clothing Collection</h1>
        <p>Discover the latest fashion trends and styles for all seasons</p>
        <input type="text" placeholder="Search clothing collection..." className="search-bar" />
      </header>

      <section className="featured-clothing">
        <h2>Featured Clothing</h2>
        <div className="item-grid">
          {featuredClothing.map((item, index) => (
            <div className="item-card" key={index}>
              <h3>{item.name}</h3>
              <p>${item.price.toFixed(2)}</p>
              <button className="add-to-cart">Add to Cart</button>
              <button className="details">Details</button>
            </div>
          ))}
        </div>
      </section>

      <section className="new-arrivals">
        <h2>New Arrivals</h2>
        <div className="image-grid">
          {newArrivals.map((item, index) => (
            <img key={index} src={item.image} alt={item.name} />
          ))}
        </div>
      </section>

      <section className="shop-by-category">
        <h2>Shop by Category</h2>
        <ul>
          {shopByCategory.map((category, index) => (
            <li key={index}>
              <strong>{category.name}</strong>
              <ul>
                {category.subcategories.map((subcategory, subIndex) => (
                  <li key={subIndex}>{subcategory}</li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </section>

      <section className="popular-brands">
        <h2>Popular Brands</h2>
        <div className="brand-grid">
          {popularBrands.map((brand, index) => (
            <img key={index} src={brand.image} alt={brand.name} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default ClothingCollection;
