import React, { useState, useEffect } from 'react';
import './Cart.css';
import { Link, useNavigate } from 'react-router-dom';
import { customerAPI, isCustomerLoggedIn } from '../../utils/api';
import MainLayout from '../layout/MainLayout';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [cartSummary, setCartSummary] = useState({ totalItems: 0, subtotal: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    if (!isCustomerLoggedIn()) {
      navigate('/vendi/login', { state: { from: '/vendi/cart' } });
      return;
    }

    // Fetch cart data
    fetchCartData();
  }, [navigate]);

  const fetchCartData = async () => {
    try {
      setLoading(true);
      const response = await customerAPI.getCart();
      setCartItems(response.data.cartItems);
      setCartSummary(response.data.cartSummary);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching cart:', err);
      setError('Failed to load cart. Please try again.');
      setLoading(false);
    }
  };

  const handleUpdateQuantity = async (cartItemId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      await customerAPI.updateCartItem(cartItemId, newQuantity);
      // Refresh cart data
      fetchCartData();
    } catch (err) {
      console.error('Error updating cart:', err);
      setError(err.response?.data?.error || 'Failed to update cart. Please try again.');
    }
  };

  const handleRemoveItem = async (cartItemId) => {
    try {
      await customerAPI.removeFromCart(cartItemId);
      // Refresh cart data
      fetchCartData();
    } catch (err) {
      console.error('Error removing item:', err);
      setError('Failed to remove item. Please try again.');
    }
  };

  const handleContinueShopping = () => {
    navigate('/vendi/all-products');
  };

  const handleCheckout = () => {
    navigate('/vendi/checkout');
  };

  // Calculate totals
  const subtotal = parseFloat(cartSummary.subtotal) || 0;
  const shipping = 0; // Assuming free shipping
  const total = subtotal + shipping;

  return (
    <MainLayout userType="customer" pageTitle="Your Shopping Cart" showSearchBar={true}>
      <div className="cart-container">
        {loading ? (
          <div className="loading-spinner">Loading cart...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : cartItems.length === 0 ? (
          <div className="empty-cart">
            <h2>Your cart is empty</h2>
            <p>Looks like you haven't added any products to your cart yet.</p>
            <button className="btn btn-primary" onClick={handleContinueShopping}>
              Start Shopping
            </button>
          </div>
        ) : (
          <>
            <div className="cart-items">
              <p>{cartSummary.totalItems} items in your cart</p>
              <ul>
                {cartItems.map(item => (
                  <li key={item.cart_item_id} className="cart-item">
                    <div className="item-image">
                      {item.image_url ? (
                        <img src={item.image_url} alt={item.name} />
                      ) : (
                        <div className="placeholder-image">No Image</div>
                      )}
                    </div>
                    <div className="item-details">
                      <h3>{item.name}</h3>
                      <p className="item-price">Price: ${parseFloat(item.price).toFixed(2)}</p>
                      <div className="quantity-controls">
                        <button 
                          onClick={() => handleUpdateQuantity(item.cart_item_id, item.quantity - 1)}
                          disabled={item.quantity <= 1}
                        >
                          -
                        </button>
                        <span>{item.quantity}</span>
                        <button 
                          onClick={() => handleUpdateQuantity(item.cart_item_id, item.quantity + 1)}
                          disabled={item.quantity >= item.stock_quantity}
                        >
                          +
                        </button>
                      </div>
                      <p className="stock-info">
                        {item.stock_quantity <= 5 ? 
                          `Only ${item.stock_quantity} left in stock!` : 
                          `In stock (${item.stock_quantity} available)`}
                      </p>
                    </div>
                    <div className="item-total">
                      <p>Total: ${parseFloat(item.total_price).toFixed(2)}</p>
                      <button 
                        className="remove-item" 
                        onClick={() => handleRemoveItem(item.cart_item_id)}
                      >
                        Remove
                      </button>
                    </div>
                  </li>
                ))}
              </ul>
            </div>

            <div className="cart-actions">
              <button className="continue-shopping" onClick={handleContinueShopping}>
                Continue Shopping
              </button>
            </div>

            <div className="order-summary">
              <h2>Order Summary</h2>
              <div className="summary-details">
                <div className="summary-row">
                  <p>Subtotal</p>
                  <p>${subtotal.toFixed(2)}</p>
                </div>
                <div className="summary-row">
                  <p>Shipping</p>
                  <p>Free</p>
                </div>
                <div className="summary-row total">
                  <p>Total</p>
                  <p>${total.toFixed(2)}</p>
                </div>
              </div>
              <button className="proceed-to-checkout" onClick={handleCheckout}>
                Proceed to Checkout
              </button>
            </div>

            <div className="recommended-items">
              <h2>You May Also Like</h2>
              <div className="item-list">
                <div className="item">
                  <h3>Laptop Ultra</h3>
                  <p>$1299.00</p>
                  <button className="add-to-cart">Add to Cart</button>
                </div>
                <div className="item">
                  <h3>Gaming Headset</h3>
                  <p>$149.00</p>
                  <button className="add-to-cart">Add to Cart</button>
                </div>
                <div className="item">
                  <h3>Wireless Charger</h3>
                  <p>$39.00</p>
                  <button className="add-to-cart">Add to Cart</button>
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </MainLayout>
  );
};

export default Cart;
